sourcesContent:
import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css';

function Dashboard() {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [activeSection, setActiveSection] = useState('overview');
  const [menuOpen, setMenuOpen] = useState(false);

  // Chat and video call states
  const [chatOpen, setChatOpen] = useState(false);
  const [videoCallActive, setVideoCallActive] = useState(false);
  const [selectedContact, setSelectedContact] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const messagesEndRef = useRef(null);
  const localVideoRef = useRef(null);
  const remoteVideoRef = useRef(null);
  const [isMuted, setIsMuted] = useState(false);
  const [isVideoOff, setIsVideoOff] = useState(false);
  const [callTime, setCallTime] = useState(0);
  const timerRef = useRef(null);

  // Sample data for dashboard
  const courses = [
    { id: 1, name: 'Mathematics', progress: 68, instructor: 'Dr. Smith', nextLesson: 'Calculus II' },
    { id: 2, name: 'Computer Science', progress: 42, instructor: 'Prof. Johnson', nextLesson: 'Data Structures' },
    { id: 3, name: 'Physics', progress: 85, instructor: 'Dr. Davis', nextLesson: 'Quantum Mechanics' },
    { id: 4, name: 'History', progress: 30, instructor: 'Prof. Williams', nextLesson: 'World War II' },
  ];

  const assignments = [
    { id: 1, title: 'Math Assignment 4', dueDate: '2023-04-15', status: 'pending', course: 'Mathematics' },
    { id: 2, title: 'CS Project', dueDate: '2023-04-18', status: 'completed', course: 'Computer Science' },
    { id: 3, title: 'Physics Lab Report', dueDate: '2023-04-10', status: 'overdue', course: 'Physics' },
    { id: 4, title: 'History Essay', dueDate: '2023-04-22', status: 'pending', course: 'History' },
  ];

  // Sample data for contacts with their IDs
  const contactsData = [
    { userId: 'STU2023001', name: 'Dr. Smith', role: 'Professor', avatar: 'S', status: 'online', unread: 2, lastMessage: 'Good question. For that particular problem, start with the product rule...' },
    { userId: 'STU2023002', name: 'Jenny Williams', role: 'Student', avatar: 'J', status: 'online', unread: 0, lastMessage: '7 PM at the library, usual spot!' },
    { userId: 'STU2023003', name: 'Prof. Johnson', role: 'Professor', avatar: 'P', status: 'offline', unread: 0, lastMessage: 'See you in class tomorrow.' },
    { userId: 'STU2023004', name: 'Mike Chen', role: 'Student', avatar: 'M', status: 'online', unread: 5, lastMessage: 'Thanks! I appreciate it.' },
    { userId: 'STU2023005', name: 'Sarah Davis', role: 'Teaching Assistant', avatar: 'S', status: 'away', unread: 0, lastMessage: 'The lab materials are ready for tomorrow.' },
  ];

  // Sample messages data
  const sampleMessages = {
    'STU2023001': [
      { id: 1, senderId: 'STU2023001', text: 'Hello, how are your math assignments coming along?', timestamp: '2023-04-05T10:30:00' },
      { id: 2, senderId: 'me', text: 'Hi Dr. Smith! I\'ve completed the first part, but I have a question about the calculus problem.', timestamp: '2023-04-05T10:32:00' },
      { id: 3, senderId: 'STU2023001', text: 'Sure, what\'s your question?', timestamp: '2023-04-05T10:33:00' },
      { id: 4, senderId: 'me', text: 'In problem 3, I\'m not sure if I should use the chain rule or product rule first.', timestamp: '2023-04-05T10:35:00' },
      { id: 5, senderId: 'STU2023001', text: 'Good question. For that particular problem, start with the product rule, then apply the chain rule to the second term.', timestamp: '2023-04-05T10:38:00' },
    ],
    'STU2023002': [
      { id: 1, senderId: 'STU2023002', text: 'Hey! Are you coming to the study group tonight?', timestamp: '2023-04-04T18:20:00' },
      { id: 2, senderId: 'me', text: 'Yes, I\'ll be there. What time are we meeting?', timestamp: '2023-04-04T18:22:00' },
      { id: 3, senderId: 'STU2023002', text: '7 PM at the library, usual spot!', timestamp: '2023-04-04T18:23:00' },
    ],
    'STU2023004': [
      { id: 1, senderId: 'STU2023004', text: 'Did you get the notes from yesterday\'s class?', timestamp: '2023-04-01T09:15:00' },
      { id: 2, senderId: 'me', text: 'Yes, I did. Do you need them?', timestamp: '2023-04-01T09:20:00' },
      { id: 3, senderId: 'STU2023004', text: 'That would be great! Can you share them?', timestamp: '2023-04-01T09:21:00' },
      { id: 4, senderId: 'me', text: 'Sure, I\'ll send them after class today.', timestamp: '2023-04-01T09:25:00' },
      { id: 5, senderId: 'STU2023004', text: 'Thanks! I appreciate it.', timestamp: '2023-04-01T09:26:00' },
    ]
  };

  const announcements = [
    { id: 1, title: 'Spring Break Schedule', date: '2023-03-30', content: 'Spring break will be from April 8 to April 16.' },
    { id: 2, title: 'New Learning Resources', date: '2023-04-02', content: 'Check out the new learning resources available in the library.' },
    { id: 3, title: 'Campus Event', date: '2023-04-05', content: 'Join us for the annual science fair on April 20.' },
  ];

  useEffect(() => {
    // Check if user is logged in
    const loggedInUser = localStorage.getItem('user');
    if (loggedInUser) {
      const foundUser = JSON.parse(loggedInUser);
      // Ensure the user has a student ID
      if (!foundUser.studentId) {
        foundUser.studentId = 'STU2023012'; // Default ID for demo purposes
      }
      setUser(foundUser);
    } else {
      // Redirect to login if not logged in
      navigate('/signin');
    }

    // Simulate loading data
    setTimeout(() => {
      setIsLoading(false);
    }, 1200);
  }, [navigate]);

  useEffect(() => {
    // Scroll to bottom of messages when new messages are added
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/signin');
  };

  const openChat = (contactId = null) => {
    setChatOpen(true);
    if (contactId) {
      const contact = contactsData.find(c => c.userId === contactId);
      if (contact) {
        setSelectedContact(contact);
        setMessages(sampleMessages[contactId] || []);
        // Mark messages as read
        const updatedContacts = contactsData.map(c => 
          c.userId === contactId ? {...c, unread: 0} : c
        );
        // In a real app, you'd update the contactsData state here
      }
    }
  };

  const closeChat = () => {
    setChatOpen(false);
    setSelectedContact(null);
  };

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (!newMessage.trim() || !selectedContact) return;

    // Add new message to the conversation
    const newMsg = {
      id: messages.length + 1,
      senderId: 'me',
      text: newMessage,
      timestamp: new Date().toISOString(),
    };

    setMessages([...messages, newMsg]);
    setNewMessage('');

    // Simulate response (only for demo purposes)
    if (selectedContact.userId === 'STU2023001') {
      setTimeout(() => {
        const response = {
          id: messages.length + 2,
          senderId: selectedContact.userId,
          text: 'I see. Let me know if you have any other questions!',
          timestamp: new Date().toISOString(),
        };
        setMessages(prevMessages => [...prevMessages, response]);
      }, 3000);
    }
  };

  const formatTime = (timestamp) => {
    const date = new Date(timestamp);
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  const formatCallTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const startVideoCall = (contactId) => {
    const contact = contactsData.find(c => c.userId === contactId);
    if (contact) {
      setSelectedContact(contact);
      setVideoCallActive(true);

      // Start timer for call duration
      timerRef.current = setInterval(() => {
        setCallTime(prev => prev + 1);
      }, 1000);

      // Initialize video call
      initializeVideoCall();
    }
  };

  const initializeVideoCall = async () => {
    try {
      // Get local video stream
      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: true, 
        audio: true 
      });

      if (localVideoRef.current) {
        localVideoRef.current.srcObject = stream;
      }

      // For demo purposes, we're not actually connecting to anyone
      // In a real app, you'd use WebRTC to establish a connection

    } catch (error) {
      console.error("Error accessing media devices:", error);
      alert("Unable to access camera or microphone. Please check permissions.");
      endCall();
    }
  };

  const toggleMute = () => {
    setIsMuted(!isMuted);
    if (localVideoRef.current && localVideoRef.current.srcObject) {
      const audioTracks = localVideoRef.current.srcObject.getAudioTracks();
      audioTracks.forEach(track => {
        track.enabled = isMuted;
      });
    }
  };

  const toggleVideo = () => {
    setIsVideoOff(!isVideoOff);
    if (localVideoRef.current && localVideoRef.current.srcObject) {
      const videoTracks = localVideoRef.current.srcObject.getVideoTracks();
      videoTracks.forEach(track => {
        track.enabled = isVideoOff;
      });
    }
  };

  const endCall = () => {
    if (timerRef.current) clearInterval(timerRef.current);
    setCallTime(0);

    // Stop all tracks
    if (localVideoRef.current && localVideoRef.current.srcObject) {
      localVideoRef.current.srcObject.getTracks().forEach(track => track.stop());
    }

    setVideoCallActive(false);
  };

  if (isLoading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading your dashboard...</p>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <div className={`sidebar ${menuOpen ? 'open' : ''}`}>
        <div className="sidebar-header">
          <div className="logo">StudyPortal</div>
          <button className="close-menu" onClick={() => setMenuOpen(false)}></button>
        </div>
        <div className="user-info">
          <div className="avatar">{user?.name?.charAt(0)}</div>
          <div className="user-details">
            <h3>{user?.name}</h3>
            <p>Student ID: {user?.studentId || 'STU2023012'}</p>
          </div>
        </div>
        <nav className="sidebar-nav">
          <ul>
            <li className={activeSection === 'overview' ? 'active' : ''}>
              <button onClick={() => setActiveSection('overview')}>Dashboard</button>
            </li>
            <li className={activeSection === 'courses' ? 'active' : ''}>
              <button onClick={() => setActiveSection('courses')}>My Courses</button>
            </li>
            <li className={activeSection === 'assignments' ? 'active' : ''}>
              <button onClick={() => setActiveSection('assignments')}>Assignments</button>
            </li>
            <li className={activeSection === 'calendar' ? 'active' : ''}>
              <button onClick={() => setActiveSection('calendar')}>Calendar</button>
            </li>
            <li className={activeSection === 'messages' ? 'active' : ''}>
              <button onClick={() => setActiveSection('messages')}>Messages</button>
            </li>
            <li className={activeSection === 'resources' ? 'active' : ''}>
              <button onClick={() => setActiveSection('resources')}>Resources</button>
            </li>
            <li className={activeSection === 'settings' ? 'active' : ''}>
              <button onClick={() => setActiveSection('settings')}>Settings</button>
            </li>
          </ul>
        </nav>
        <button className="logout-button" onClick={handleLogout}>Logout</button>
      </div>

   {/* Main Content */}
   <div className="main-content">
        {/* Header */}
        <header className="dashboard-header">
          <button className="menu-toggle" onClick={() => setMenuOpen(true)}>

          </button>
          <h1>{activeSection.charAt(0).toUpperCase() + activeSection.slice(1)}</h1>
          <div className="header-actions">
            <button className="chat-button" onClick={() => navigateToChat()}>

              {contactsData.reduce((count, contact) => count + contact.unread, 0) > 0 && (
                <span className="notification-badge">
                  {contactsData.reduce((count, contact) => count + contact.unread, 0)}
                </span>
              )}
            </button>
            <button className="notification-bell">

              <span className="notification-badge">3</span>
            </button>
          </div>
        </header>

        {/* Content Sections */}
        <div className="dashboard-content">
          {activeSection === 'overview' && (
            <div className="section-overview animate-fade-in">
              <div className="welcome-banner">
                <h2>Welcome back, {user?.name}!</h2>
                <p>Here's what's happening with your courses today.</p>
              </div>

              <div className="stats-container">
                <div className="stat-card">
                  <h3>4</h3>
                  <p>Active Courses</p>
                </div>
                <div className="stat-card">
                  <h3>3</h3>
                  <p>Pending Assignments</p>
                </div>
                <div className="stat-card">
                  <h3>85%</h3>
                  <p>Average Attendance</p>
                </div>
                <div className="stat-card">
                  <h3>B+</h3>
                  <p>Average Grade</p>
                </div>
              </div>

              <div className="dashboard-grid">
                <div className="dashboard-card courses-preview">
                  <div className="card-header">
                    <h3>My Courses</h3>
                    <button onClick={() => setActiveSection('courses')}>View All</button>
                  </div>
                  <div className="courses-list">
                    {courses.slice(0, 3).map(course => (
                      <div className="course-item" key={course.id}>
                        <div className="course-info">
                          <h4>{course.name}</h4>
                          <p>Next: {course.nextLesson}</p>
                        </div>
                        <div className="course-progress">
                          <div className="progress-bar">
                            <div className="progress" style={{ width: `${course.progress}%` }}></div>
                          </div>
                          <span>{course.progress}%</span>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

                <div className="dashboard-card upcoming-assignments">
                  <div className="card-header">
                    <h3>Upcoming Assignments</h3>
                    <button onClick={() => setActiveSection('assignments')}>View All</button>
                  </div>
                  <div className="assignments-list">
                    {assignments.filter(a => a.status === 'pending').slice(0, 3).map(assignment => (
                      <div className="assignment-item" key={assignment.id}>
                        <h4>{assignment.title}</h4>
                        <div className="assignment-details">
                          <span className="assignment-course">{assignment.course}</span>
                          <span className="assignment-due">Due: {new Date(assignment.dueDate).toLocaleDateString()}</span>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

                <div className="dashboard-card recent-messages">
                  <div className="card-header">
                    <h3>Recent Messages</h3>
                    <button onClick={() => navigateToChat()}>View All</button>
                  </div>
                  <div className="messages-preview">
                    {contactsData.filter(contact => contact.unread > 0 || contact.lastMessage).slice(0, 3).map(contact => (
                      <div className="message-preview-item" key={contact.userId}>
                        <div className="contact-avatar">
                          {contact.avatar}
                          <span className={`status-indicator ${contact.status}`}></span>
                        </div>
                        <div className="message-preview-content">
                          <div className="message-preview-header">
                            <h4>{contact.name}</h4>
                            <div className="message-actions">
                              <button className="chat-action" onClick={() => navigateToChat(contact.userId)}>

                                {contact.unread > 0 && (
                                  <span className="unread-badge">{contact.unread}</span>
                                )}
                              </button>
                              <button className="video-action" onClick={() => startVideoCall(contact.userId)}></button>
                            </div>
                          </div>
                          <p className="message-preview-text">{contact.lastMessage}</p>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          )}

          {activeSection === 'messages' && (
            <div className="section-messages animate-fade-in">
              <div className="section-header">
                <h2>Messages</h2>
                <div className="message-actions-container">
                  <button className="action-button" onClick={() => navigateToChat()}>Open Chat Interface</button>
                  <select defaultValue="all">
                    <option value="all">All Contacts</option>
                    <option value="online">Online Only</option>
                    <option value="unread">Unread Messages</option>
                  </select>
                </div>
              </div>

              <div className="contacts-grid">
                {contactsData.map(contact => (
                  <div className="contact-card" key={contact.userId}>
                    <div className="contact-header">
                      <div className="contact-avatar-large">
                        {contact.avatar}
                        <span className={`status-indicator-large ${contact.status}`}></span>
                      </div>
                      <div className="contact-header-info">
                        <h3>{contact.name}</h3>
                        <p>{contact.role}</p>
                        <p className="contact-id">ID: {contact.userId}</p>
                      </div>
                    </div>

                    <div className="contact-preview">
                      {contact.lastMessage && (
                        <p className="last-message">
                          {contact.unread > 0 && <span className="unread-marker"></span>}
                          {contact.lastMessage}
                        </p>
                      )}
                    </div>

                    <div className="contact-actions">
                      <button className="message-btn" onClick={() => navigateToChat(contact.userId)}>
                        Message
                        {contact.unread > 0 && (
                          <span className="unread-badge">{contact.unread}</span>
                        )}
                      </button>
                      <button 
                        className="video-call-btn" 
                        onClick={() => startVideoCall(contact.userId)}
                        disabled={contact.status === 'offline'}
                      >
                        Video Call
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

{activeSection === 'courses' && (
            <div className="section-courses animate-fade-in">
              <div className="section-header">
                <h2>My Courses</h2>
                <div className="course-filter">
                  <select defaultValue="all">
                    <option value="all">All Courses</option>
                    <option value="inProgress">In Progress</option>
                    <option value="completed">Completed</option>
                  </select>
                </div>
              </div>

              <div className="courses-grid">
                {courses.map(course => (
                  <div className="course-card" key={course.id}>
                    <div className="course-header">
                      <h3>{course.name}</h3>
                    </div>
                    <div className="course-details">
                      <p><strong>Instructor:</strong> {course.instructor}</p>
                      <p><strong>Next Lesson:</strong> {course.nextLesson}</p>
                      <div className="course-progress-container">
                        <p>Progress: {course.progress}%</p>
                        <div className="progress-bar">
                          <div className="progress" style={{ width: `${course.progress}%` }}></div>
                        </div>
                      </div>
                    </div>
                    <div className="course-actions">
                      <button className="primary-button">Continue Learning</button>
                      <button className="secondary-button">Course Details</button>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {activeSection === 'assignments' && (
            <div className="section-assignments animate-fade-in">
              <div className="section-header">
                <h2>Assignments</h2>
                <div className="assignment-filter">
                  <select defaultValue="all">
                    <option value="all">All Assignments</option>
                    <option value="pending">Pending</option>
                    <option value="completed">Completed</option>
                    <option value="overdue">Overdue</option>
                  </select>
                </div>
              </div>

              <div className="assignments-grid">
                {assignments.map(assignment => (
                  <div className={`assignment-card ${assignment.status}`} key={assignment.id}>
                    <div className="assignment-status-indicator"></div>
                    <div className="assignment-content">
                      <h3>{assignment.title}</h3>
                      <p className="assignment-course-name">{assignment.course}</p>
                      <div className="assignment-meta">
                        <p className="due-date">
                          <span>Due: </span>
                          {new Date(assignment.dueDate).toLocaleDateString()}
                        </p>
                        <p className="status-text">
                          Status: <span className="status">{assignment.status}</span>
                        </p>
                      </div>
                    </div>
                    <div className="assignment-actions">
                      {assignment.status === 'pending' && (
                        <button className="submit-button">Submit Assignment</button>
                      )}
                      {assignment.status === 'completed' && (
                        <button className="view-grade-button">View Grade</button>
                      )}
                      {assignment.status === 'overdue' && (
                        <button className="contact-button">Contact Instructor</button>
                      )}
                      <button className="details-button">View Details</button>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {activeSection === 'calendar' && (
            <div className="section-calendar animate-fade-in">
              <div className="section-header">
                <h2>Calendar</h2>
                <div className="calendar-actions">
                  <button className="today-button">Today</button>
                  <div className="nav-buttons">
                    <button className="prev-button"></button>
                    <span className="current-month">April 2023</span>
                    <button className="next-button"></button>
                  </div>
                  <select defaultValue="month">
                    <option value="day">Day</option>
                    <option value="week">Week</option>
                    <option value="month">Month</option>
                  </select>
                </div>
              </div>

              <div className="calendar-container">
                <div className="calendar-grid">
                  {/* Placeholder for calendar implementation */}
                  <div className="calendar-placeholder">
                    <p>Calendar view would be implemented here with a proper calendar library like FullCalendar or react-big-calendar</p>
                    <p>Events would include class schedules, assignment due dates, and exams</p>
                  </div>
                </div>
              </div>
            </div>
          )}

          {activeSection === 'resources' && (
            <div className="section-resources animate-fade-in">
              <div className="section-header">
                <h2>Learning Resources</h2>
                <div className="resource-search">
                  <input type="text" placeholder="Search resources..." />
                  <button className="search-button">Search</button>
                </div>
              </div>

              <div className="resources-categories">
                <div className="category-card">
                  <div className="category-icon"></div>
                  <h3>Course Materials</h3>
                  <p>Textbooks, slides, and lecture notes</p>
                  <button>Browse</button>
                </div>
                <div className="category-card">
                  <div className="category-icon"></div>
                  <h3>Video Lectures</h3>
                  <p>Recorded lectures and tutorials</p>
                  <button>Browse</button>
                </div>
                <div className="category-card">
                  <div className="category-icon"></div>
                  <h3>Practice Tests</h3>
                  <p>Sample exams and quizzes</p>
                  <button>Browse</button>
                </div>
                <div className="category-card">
                  <div className="category-icon"></div>
                  <h3>Lab Resources</h3>
                  <p>Lab manuals and simulation tools</p>
                  <button>Browse</button>
                </div>
              </div>

              <div className="recent-resources">
                <h3>Recently Accessed</h3>
                <div className="resource-list">
                  <div className="resource-item">
                    <div className="resource-icon"></div>
                    <div className="resource-info">
                      <h4>Calculus II - Lecture Notes</h4>
                      <p>Mathematics - Accessed 2 days ago</p>
                    </div>
                    <button className="download-button">Download</button>
                  </div>
                  <div className="resource-item">
                    <div className="resource-icon"></div>
                    <div className="resource-info">
                      <h4>Data Structures Tutorial</h4>
                      <p>Computer Science - Accessed 5 days ago</p>
                    </div>
                    <button className="view-button">View</button>
                  </div>
                  <div className="resource-item">
                    <div className="resource-icon"></div>
                    <div className="resource-info">
                      <h4>Physics Lab Report Template</h4>
                      <p>Physics - Accessed 1 week ago</p>
                    </div>
                    <button className="download-button">Download</button>
                  </div>
                </div>
              </div>
            </div>
          )}

          {activeSection === 'settings' && (
            <div className="section-settings animate-fade-in">
              <div className="section-header">
                <h2>Settings</h2>
              </div>

              <div className="settings-container">
                <div className="settings-sidebar">
                  <ul className="settings-nav">
                    <li className="active">Profile</li>
                    <li>Notifications</li>
                    <li>Privacy</li>
                    <li>Account</li>
                    <li>Appearance</li>
                    <li>Help & Support</li>
                  </ul>
                </div>

                <div className="settings-content">
                  <div className="settings-section profile-settings">
                    <h3>Profile Settings</h3>

                    <div className="profile-picture-section">
                      <div className="current-avatar">{user?.name?.charAt(0)}</div>
                      <div className="avatar-actions">
                        <button className="upload-avatar">Upload New Picture</button>
                        <button className="remove-avatar">Remove</button>
                      </div>
                    </div>

                    <form className="profile-form">
                      <div className="form-group">
                        <label>Full Name</label>
                        <input type="text" defaultValue={user?.name} />
                      </div>

                      <div className="form-group">
                        <label>Student ID</label>
                        <input type="text" defaultValue={user?.studentId || 'STU2023012'} disabled />
                      </div>

                      <div className="form-group">
                        <label>Email Address</label>
                        <input type="email" defaultValue={user?.email} />
                      </div>

                      <div className="form-group">
                        <label>Phone Number</label>
                        <input type="tel" placeholder="Enter phone number" />
                      </div>

                      <div className="form-group">
                        <label>Bio</label>
                        <textarea placeholder="Tell us about yourself..."></textarea>
                      </div>

                      <div className="form-actions">
                        <button type="submit" className="save-button">Save Changes</button>
                        <button type="reset" className="cancel-button">Cancel</button>
                      </div>
                    </form>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
