import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css';
import './'

function Dashboard() {
  const navigate = useNavigate();
  const [user, setUser ] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [activeSection, setActiveSection] = useState('overview');
  const [menuOpen, setMenuOpen] = useState(false);

  // Chat and video call states
  const [chatOpen, setChatOpen] = useState(false);
  const [videoCallActive, setVideoCallActive] = useState(false);
  const [selectedContact, setSelectedContact] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const messagesEndRef = useRef(null);
  const localVideoRef = useRef(null);
  const remoteVideoRef = useRef(null);
  const [isMuted, setIsMuted] = useState(false);
  const [isVideoOff, setIsVideoOff] = useState(false);
  const [callTime, setCallTime] = useState(0);
  const timerRef = useRef(null);

  // User-entered data states
  const [courses, setCourses] = useState([]);
  const [assignments, setAssignments] = useState([]);
  const [todoItems, setTodoItems] = useState([]);

  // New course form
  const [newCourse, setNewCourse] = useState({
    name: '',
    instructor: '',
    nextLesson: '',
    progress: 0
  });

  // New assignment form
  const [newAssignment, setNewAssignment] = useState({
    title: '',
    dueDate: '',
    course: '',
    status: 'pending'
  });

  // New todo form
  const [newTodo, setNewTodo] = useState({
    title: '',
    priority: 'medium',
    dueDate: '',
    completed: false
  });

  // Sample data for contacts with their IDs
  const contactsData = [
    { userId: 'STU2023001', name: 'Dr. Smith', role: 'Professor', avatar: 'S', status: 'online', unread: 2, lastMessage: 'Good question. For that particular problem, start with the product rule...' },
    { userId: 'STU2023002', name: 'Jenny Williams', role: 'Student', avatar: 'J', status: 'online', unread: 0, lastMessage: '7 PM at the library, usual spot!' },
    { userId: 'STU2023003', name: 'Prof. Johnson', role: 'Professor', avatar: 'P', status: 'offline', unread: 0, lastMessage: 'See you in class tomorrow.' },
    { userId: 'STU2023004', name: 'Mike Chen', role: 'Student', avatar: 'M', status: 'online', unread: 5, lastMessage: 'Thanks! I appreciate it.' },
    { userId: 'STU2023005', name: 'Sarah Davis', role: 'Teaching Assistant', avatar: 'S', status: 'away', unread: 0, lastMessage: 'The lab materials are ready for tomorrow.' },
  ];

  // Sample messages data
  const sampleMessages = {
    'STU2023001': [
      { id: 1, senderId: 'STU2023001', text: 'Hello, how are your math assignments coming along?', timestamp: '2023-04-05T10:30:00' },
      { id: 2, senderId: 'me', text: 'Hi Dr. Smith! I\'ve completed the first part, but I have a question about the calculus problem.', timestamp: '2023-04-05T10:32:00' },
      { id: 3, senderId: 'STU2023001', text: 'Sure, what\'s your question?', timestamp: '2023-04-05T10:33:00' },
      { id: 4, senderId: 'me', text: 'In problem 3, I\'m not sure if I should use the chain rule or product rule first.', timestamp: '2023-04-05T10:35:00' },
      { id: 5, senderId: 'STU2023001', text: 'Good question. For that particular problem, start with the product rule, then apply the chain rule to the second term.', timestamp: '2023-04-05T10:38:00' },
    ],
    'STU2023002': [
      { id: 1, senderId: 'STU2023002', text: 'Hey! Are you coming to the study group tonight?', timestamp: '2023-04-04T18:20:00' },
      { id: 2, senderId: 'me', text: 'Yes, I\'ll be there. What time are we meeting?', timestamp: '2023-04-04T18:22:00' },
      { id: 3, senderId: 'STU2023002', text: '7 PM at the library, usual spot!', timestamp: '2023-04-04T18:23:00' },
    ],
    'STU2023004': [
      { id: 1, senderId: 'STU2023004', text: 'Did you get the notes from yesterday\'s class?', timestamp: '2023-04-01T09:15:00' },
      { id: 2, senderId: 'me', text: 'Yes, I did. Do you need them?', timestamp: '2023-04-01T09:20:00' },
      { id: 3, senderId: 'STU2023004', text: 'That would be great! Can you share them?', timestamp: '2023-04-01T09:21:00' },
      { id: 4, senderId: 'me', text: 'Sure, I\'ll send them after class today.', timestamp: '2023-04-01T09:25:00' },
      { id: 5, senderId: 'STU2023004', text: 'Thanks! I appreciate it.', timestamp: '2023-04-01T09:26:00' },
    ]
  };

  const announcements = [
    { id: 1, title: 'Spring Break Schedule', date: '2023-03-30', content: 'Spring break will be from April 8 to April 16.' },
    { id: 2, title: 'New Learning Resources', date: '2023-04-02', content: 'Check out the new learning resources available in the library.' },
    { id: 3, title: 'Campus Event', date: '2023-04-05', content: 'Join us for the annual science fair on April 20.' },
  ];

  useEffect(() => {
    // Check if user is logged in
    const loggedInUser  = localStorage.getItem('user');
    if (loggedInUser ) {
      const foundUser  = JSON.parse(loggedInUser );
      // Ensure the user has a student ID
      if (!foundUser .studentId) {
        foundUser .studentId = 'STU2023012'; // Default ID for demo purposes
      }
      setUser (foundUser );
    } else {
      // Redirect to login if not logged in
      navigate('/signin');
    }

    // Load saved user data from localStorage
    const savedCourses = localStorage.getItem('userCourses');
    if (savedCourses) {
      setCourses(JSON.parse(savedCourses));
    }

    const savedAssignments = localStorage.getItem('userAssignments');
    if (savedAssignments) {
      setAssignments(JSON.parse(savedAssignments));
    }

    const savedTodos = localStorage.getItem('userTodos');
    if (savedTodos) {
      setTodoItems(JSON.parse(savedTodos));
    }

    // Simulate loading data
    setTimeout(() => {
      setIsLoading(false);
    }, 1200);
  }, [navigate]);

  // Save data to localStorage whenever it changes
  useEffect(() => {
    if (courses.length > 0) {
      localStorage.setItem('userCourses', JSON.stringify(courses));
    }
  }, [courses]);

  useEffect(() => {
    if (assignments.length > 0) {
      localStorage.setItem('userAssignments', JSON.stringify(assignments));
    }
  }, [assignments]);

  useEffect(() => {
    if (todoItems.length > 0) {
      localStorage.setItem('userTodos', JSON.stringify(todoItems));
    }
  }, [todoItems]);

  useEffect(() => {
    // Scroll to bottom of messages when new messages are added
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/signin');
  };

  const openChat = (contactId = null) => {
    setChatOpen(true);
    if (contactId) {
      const contact = contactsData.find(c => c.userId === contactId);
      if (contact) {
        setSelectedContact(contact);
        setMessages(sampleMessages[contactId] || []);
        // Mark messages as read
        const updatedContacts = contactsData.map(c => 
          c.userId === contactId ? {...c, unread: 0} : c
        );
        // In a real app, you'd update the contactsData state here
      }
    }
  };

  const closeChat = () => {
    setChatOpen(false);
    setSelectedContact(null);
  };

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (!newMessage.trim() || !selectedContact) return;

    // Add new message to the conversation
    const newMsg = {
      id: messages.length + 1,
      senderId: 'me',
      text: newMessage,
      timestamp: new Date().toISOString(),
    };

    setMessages([...messages, newMsg]);
    setNewMessage('');

    // Simulate response (only for demo purposes)
    if (selectedContact.userId === 'STU2023001') {
      setTimeout(() => {
        const response = {
          id: messages.length + 2,
          senderId: selectedContact.userId,
          text: 'I see. Let me know if you have any other questions!',
          timestamp: new Date().toISOString(),
        };
        setMessages(prevMessages => [...prevMessages, response]);
      }, 3000);
    }
  };

  const formatTime = (timestamp) => {
    const date = new Date(timestamp);
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  const formatCallTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const startVideoCall = (contactId) => {
    const contact = contactsData.find(c => c.userId === contactId);
    if (contact) {
      setSelectedContact(contact);
      setVideoCallActive(true);

      // Start timer for call duration
      timerRef.current = setInterval(() => {
        setCallTime(prev => prev + 1);
      }, 1000);

      // Initialize video call
      initializeVideoCall();
    }
  };

  const initializeVideoCall = async () => {
    try {
      // Get local video stream
      const stream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true
      });

      if (localVideoRef.current) {
        localVideoRef.current.srcObject = stream;
      }
    } catch (error) {
      console.error("Error accessing media devices:", error);
    }
  };
      // For demo purposes, we're not actually connecting to anyone
      // In a real app, you'd use WebRTC to establish a connection




  const toggleMute = () => {
    setIsMuted(!isMuted);
    if (localVideoRef.current && localVideoRef.current.srcObject) {
      const audioTracks = localVideoRef.current.srcObject.getAudioTracks();
      audioTracks.forEach(track => {
        track.enabled = isMuted;
      });
    }
  };

  const toggleVideo = () => {
    setIsVideoOff(!isVideoOff);
    if (localVideoRef.current && localVideoRef.current.srcObject) {
      const videoTracks = localVideoRef.current.srcObject.getVideoTracks();
      videoTracks.forEach(track => {
        track.enabled = isVideoOff;
      });
    }
  };

  const endCall = () => {
    if (timerRef.current) clearInterval(timerRef.current);
    setCallTime(0);

    // Stop all tracks
    if (localVideoRef.current && localVideoRef.current.srcObject) {
      localVideoRef.current.srcObject.getTracks().forEach(track => track.stop());
    }

    setVideoCallActive(false);
  };

  // Handle course form changes
  const handleCourseChange = (e) => {
    const { name, value } = e.target;
    setNewCourse(prev => ({
      ...prev,
      [name]: name === 'progress' ? parseInt(value) : value
    }));
  };

  // Add new course
  const handleAddCourse = (e) => {
    e.preventDefault();
    if (!newCourse.name) return;

    const course = {
      id: Date.now(),
      ...newCourse
    };

    setCourses(prev => [...prev, course]);
    setNewCourse({
      name: '',
      instructor: '',
      nextLesson: '',
      progress: 0
    });
  };

  // Handle assignment form changes
  const handleAssignmentChange = (e) => {
    const { name, value } = e.target;
    setNewAssignment(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Add new assignment
  const handleAddAssignment = (e) => {
    e.preventDefault();
    if (!newAssignment.title || !newAssignment.dueDate) return;

    const assignment = {
      id: Date.now(),
      ...newAssignment
    };

    setAssignments(prev => [...prev, assignment]);
    setNewAssignment({
      title: '',
      dueDate: '',
      course: '',
      status: 'pending'
    });
  };

  // Handle todo form changes
  const handleTodoChange = (e) => {
    const { name, value } = e.target;
    setNewTodo(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Add new todo
  const handleAddTodo = (e) => {
    e.preventDefault();
    if (!newTodo.title) return;

    const todo = {
      id: Date.now(),
      ...newTodo,
      completed: false
    };

    setTodoItems(prev => [...prev, todo]);
    setNewTodo({
      title: '',
      priority: 'medium',
      dueDate: '',
      completed: false
    });
  };

  // Toggle todo completion
  const toggleTodoCompletion = (id) => {
    setTodoItems(prev => 
      prev.map(todo => 
        todo.id === id ? { ...todo, completed: !todo.completed } : todo
      )
    );
  };

  // Update assignment status
  const updateAssignmentStatus = (id, status) => {
    setAssignments(prev => 
      prev.map(assignment => 
        assignment.id === id ? { ...assignment, status } : assignment
      )
    );
  };

  // Delete a course
  const deleteCourse = (id) => {
    setCourses(prev => prev.filter(course => course.id !== id));
  };

  // Delete an assignment
  const deleteAssignment = (id) => {
    setAssignments(prev => prev.filter(assignment => assignment.id !== id));
  };

  // Delete a todo item
  const deleteTodo = (id) => {
    setTodoItems(prev => prev.filter(todo => todo.id !== id));
  };

  // Update course progress
  const updateCourseProgress = (id, progress) => {
    setCourses(prev => 
      prev.map(course => 
        course.id === id ? { ...course, progress } : course
      )
    );
  };

  // Sort assignments by due date
  const sortedAssignments = () => {
    return [...assignments].sort((a, b) => new Date(a.dueDate) - new Date(b.dueDate));
  };

  // Get overdue assignments
  const overdueAssignments = () => {
    const today = new Date();
    return assignments.filter(assignment => 
      new Date(assignment.dueDate) < today && assignment.status === 'pending'
    );
  };

  // Get upcoming assignments (due within the next 7 days)
  const upcomingAssignments = () => {
    const today = new Date();
    const nextWeek = new Date();
    nextWeek.setDate(today.getDate() + 7);

    return assignments.filter(assignment => {
      const dueDate = new Date(assignment.dueDate);
      return dueDate >= today && dueDate <= nextWeek && assignment.status === 'pending';
    });
  };

  // Toggle mobile menu
  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  // Format date for display
  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'short', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  // Calculate overall course progress
  const calculateOverallProgress = () => {
    if (courses.length === 0) return 0;
    const totalProgress = courses.reduce((sum, course) => sum + course.progress, 0);
    return Math.round(totalProgress / courses.length);
  };

  // Get courses with upcoming lessons
  const getUpcomingLessons = () => {
    return courses.filter(course => course.nextLesson && course.nextLesson.trim() !== '');
  };

  // Render different sections based on activeSection
  const renderSection = () => {
    switch (activeSection) {
      case 'overview':
        return (
          <div className="dashboard-overview">
            <h2>Welcome back, {user?.name || 'Student'}!</h2>

            <div className="stats-container">
              <div className="stat-card">
                <h3>Courses</h3>
                <div className="stat-value">{courses.length}</div>
              </div>
              <div className="stat-card">
                <h3>Overall Progress</h3>
                <div className="stat-value">{calculateOverallProgress()}%</div>
              </div>
              <div className="stat-card">
                <h3>Assignments</h3>
                <div className="stat-value">{assignments.length}</div>
              </div>
              <div className="stat-card">
                <h3>Overdue</h3>
                <div className="stat-value">{overdueAssignments().length}</div>
              </div>
            </div>

            <div className="dashboard-sections">
              <div className="section upcoming-assignments">
                <h3>Upcoming Assignments</h3>
                {upcomingAssignments().length > 0 ? (
                  <ul className="assignment-list">
                    {upcomingAssignments().map(assignment => (
                      <li key={assignment.id} className="assignment-item">
                        <div className="assignment-title">{assignment.title}</div>
                        <div className="assignment-details">
                          <span className="due-date">Due: {formatDate(assignment.dueDate)}</span>
                          <span className="course-name">{assignment.course}</span>
                        </div>
                      </li>
                    ))}
                  </ul>
                ) : (
                  <p className="empty-message">No upcoming assignments</p>
                )}
              </div>

              <div className="section announcements">
                <h3>Announcements</h3>
                <ul className="announcement-list">
                  {announcements.map(announcement => (
                    <li key={announcement.id} className="announcement-item">
                      <div className="announcement-header">
                        <span className="announcement-title">{announcement.title}</span>
                        <span className="announcement-date">{formatDate(announcement.date)}</span>
                      </div>
                      <div className="announcement-content">{announcement.content}</div>
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          </div>
        );

      case 'courses':
        return (
          <div className="courses-section">
            <h2>My Courses</h2>

            <div className="add-course-form">
              <h3>Add New Course</h3>
              <form onSubmit={handleAddCourse}>
                <div className="form-group">
                  <input
                    type="text"
                    name="name"
                    placeholder="Course Name"
                    value={newCourse.name}
                    onChange={handleCourseChange}
                    required
                  />
                </div>
                <div className="form-group">
                  <input
                    type="text"
                    name="instructor"
                    placeholder="Instructor"
                    value={newCourse.instructor}
                    onChange={handleCourseChange}
                  />
                </div>
                <div className="form-group">
                  <input
                    type="text"
                    name="nextLesson"
                    placeholder="Next Lesson"
                    value={newCourse.nextLesson}
                    onChange={handleCourseChange}
                  />
                </div>
                <div className="form-group">
                  <label>Progress:</label>
                  <input
                    type="range"
                    name="progress"
                    min="0"
                    max="100"
                    value={newCourse.progress}
                    onChange={handleCourseChange}
                  />
                  <span>{newCourse.progress}%</span>
                </div>
                <button type="submit" className="btn-primary">Add Course</button>
              </form>
            </div>

            <div className="courses-list">
              {courses.length > 0 ? (
                courses.map(course => (
                  <div key={course.id} className="course-card">
                    <div className="course-header">
                      <h3>{course.name}</h3>
                      <button onClick={() => deleteCourse(course.id)} className="btn-delete">Delete</button>
                    </div>
                    <div className="course-details">
                      <p><strong>Instructor:</strong> {course.instructor}</p>
                      <p><strong>Next Lesson:</strong> {course.nextLesson || 'None scheduled'}</p>
                      <div className="progress-container">
                        <label>Progress: {course.progress}%</label>
                        <div className="progress-bar">
                          <div className="progress-fill" style={{ width: `${course.progress}%` }}></div>
                        </div>
                      </div>
                    </div>
                  </div>
                ))
              ) : (
                <p className="empty-message">No courses added yet</p>
              )}
            </div>
          </div>
        );

      case 'assignments':
        return (
          <div className="assignments-section">
            <h2>My Assignments</h2>

            <div className="add-assignment-form">
              <h3>Add New Assignment</h3>
              <form onSubmit={handleAddAssignment}>
                <div className="form-group">
                  <input
                    type="text"
                    name="title"
                    placeholder="Assignment Title"
                    value={newAssignment.title}
                    onChange={handleAssignmentChange}
                    required
                  />
                </div>
                <div className="form-group">
                  <input
                    type="date"
                    name="dueDate"
                    placeholder="Due Date"
                    value={newAssignment.dueDate}
                    onChange={handleAssignmentChange}
                    required
                  />
                </div>
                <div className="form-group">
                  <select
                    name="course"
                    value={newAssignment.course}
                    onChange={handleAssignmentChange}
                  >
                    <option value="">Select Course</option>
                    {courses.map(course => (
                      <option key={course.id} value={course.name}>{course.name}</option>
                    ))}
                  </select>
                </div>
                <div className="form-group">
                  <select
                    name="status"
                    value={newAssignment.status}
                    onChange={handleAssignmentChange}
                  >
                    <option value="pending">Pending</option>
                    <option value="in-progress">In Progress</option>
                    <option value="completed">Completed</option>
                  </select>
                </div>
                <button type="submit" className="btn-primary">Add Assignment</button>
              </form>
            </div>

            <div className="assignments-list">
              <h3>Assignments</h3>
              {assignments.length > 0 ? (
                <table className="assignments-table">
                  <thead>
                    <tr>
                      <th>Title</th>
                      <th>Course</th>
                      <th>Due Date</th>
                      <th>Status</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {sortedAssignments().map(assignment => (
                      <tr key={assignment.id} className={`assignment-row ${assignment.status}`}>
                        <td>{assignment.title}</td>
                        <td>{assignment.course}</td>
                        <td>{formatDate(assignment.dueDate)}</td>
                        <td>
                          <select
                            value={assignment.status}
                            onChange={(e) => updateAssignmentStatus(assignment.id, e.target.value)}
                          >
                            <option value="pending">Pending</option>
                            <option value="in-progress">In Progress</option>
                            <option value="completed">Completed</option>
                          </select>
                        </td>
                        <td>
                          <button onClick={() => deleteAssignment(assignment.id)} className="btn-delete">Delete</button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              ) : (
                <p className="empty-message">No assignments added yet</p>
              )}
            </div>
          </div>
        );

      case 'todo':
        return (
          <div className="todo-section">
            <h2>To-Do List</h2>

            <div className="add-todo-form">
              <h3>Add New Task</h3>
              <form onSubmit={handleAddTodo}>
                <div className="form-group">
                  <input
                    type="text"
                    name="title"
                    placeholder="Task Title"
                    value={newTodo.title}
                    onChange={handleTodoChange}
                    required
                  />
                </div>
                <div className="form-group">
                  <select
                    name="priority"
                    value={newTodo.priority}
                    onChange={handleTodoChange}
                  >
                    <option value="low">Low Priority</option>
                    <option value="medium">Medium Priority</option>
                    <option value="high">High Priority</option>
                  </select>
                </div>
                <div className="form-group">
                  <input
                    type="date"
                    name="dueDate"
                    placeholder="Due Date"
                    value={newTodo.dueDate}
                    onChange={handleTodoChange}
                  />
                </div>
                <button type="submit" className="btn-primary">Add Task</button>
              </form>
            </div>

            <div className="todo-list">
              <h3>Tasks</h3>
              {todoItems.length > 0 ? (
                <ul className="todo-items">
                  {todoItems.map(todo => (
                    <li key={todo.id} className={`todo-item priority-${todo.priority} ${todo.completed ? 'completed' : ''}`}>
                      <div className="todo-check">
                        <input
                          type="checkbox"
                          checked={todo.completed}
                          onChange={() => toggleTodoCompletion(todo.id)}
                        />
                      </div>
                      <div className="todo-content">
                        <div className="todo-title">{todo.title}</div>
                        {todo.dueDate && (
                          <div className="todo-due-date">Due: {formatDate(todo.dueDate)}</div>
                        )}
                      </div>
                      <div className="todo-actions">
                        <button onClick={() => deleteTodo(todo.id)} className="btn-delete">Delete</button>
                      </div>
                    </li>
                  ))}
                </ul>
              ) : (
                <p className="empty-message">No tasks added yet</p>
              )}
            </div>
          </div>
        );

      case 'contacts':
        return (
          <div className="contacts-section">
            <h2>My Contacts</h2>

            <div className="contacts-list">
              {contactsData.map(contact => (
                <div key={contact.userId} className="contact-card">
                  <div className="contact-avatar">
                    {contact.avatar}
                    <span className={`status-indicator ${contact.status}`}></span>
                  </div>
                  <div className="contact-info">
                    <h3>{contact.name}</h3>
                    <p>{contact.role}</p>
                    <p className="last-message">{contact.lastMessage}</p>
                  </div>
                  <div className="contact-actions">
                    {contact.unread > 0 && (
                      <div className="unread-badge">{contact.unread}</div>
                    )}
                    <button onClick={() => openChat(contact.userId)} className="btn-chat">Chat</button>
                    <button onClick={() => startVideoCall(contact.userId)} className="btn-video">Video</button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        );

      default:
        return <div>Select a section</div>;
    }
  };

  return (
    <div className="dashboard-container">
      {isLoading ? (
        <div className="loading-screen">
          <div className="loader"></div>
          <p>Loading your dashboard...</p>
        </div>
      ) : (
        <>
          <header className="dashboard-header">
            <div className="header-left">
              <button className="menu-toggle" onClick={toggleMenu}>
                <span className="menu-icon"></span>
              </button>
              <h1>Student Dashboard</h1>
            </div>
            <div className="header-right">
              <div className="user-profile">
                <div className="avatar">
                  {user?.name?.charAt(0) || 'S'}
                </div>
                <div className="user-info">
                  <span className="user-name">{user?.name || 'Student'}</span>
                  <span className="user-id">{user?.studentId || 'STU2023012'}</span>
                </div>
                <button className="logout-btn" onClick={handleLogout}>Logout</button>
              </div>
            </div>
          </header>

          <div className="dashboard-content">
            <nav className={`sidebar ${menuOpen ? 'open' : ''}`}>
              <ul className="nav-menu">
                <li className={activeSection === 'overview' ? 'active' : ''}>
                  <button onClick={() => setActiveSection('overview')}>Overview</button>
                </li>
                <li className={activeSection === 'courses' ? 'active' : ''}>
                  <button onClick={() => setActiveSection('courses')}>Courses</button>
                </li>
                <li className={activeSection === 'assignments' ? 'active' : ''}>
                  <button onClick={() => setActiveSection('assignments')}>Assignments</button>
                </li>
                <li className={activeSection === 'todo' ? 'active' : ''}>
                  <button onClick={() => setActiveSection('todo')}>To-Do List</button>
                </li>
                <li className={activeSection === 'contacts' ? 'active' : ''}>
                  <button onClick={() => setActiveSection('contacts')}>Contacts</button>
                </li>
              </ul>
            </nav>

            <main className="main-content">
              {renderSection()}
            </main>
          </div>

          {/* Chat window */}
          {chatOpen && selectedContact && (
            <div className="chat-window">
              {/* Chat window content here */}
            </div>
          )}

          {/* Video call overlay */}
          {videoCallActive && selectedContact && (
            <div className="video-call-overlay">
              {/* Video call content here */}
            </div>
          )}
        </>
      )}
    </div>
  );
}

export default Dashboard;
