import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const navigate = useNavigate();

  // State variables
  const [menuOpen, setMenuOpen] = useState(false);
  const [activeSection, setActiveSection] = useState('overview');
  const [user, setUser] = useState(null);
  const [courses, setCourses] = useState([]);
  const [assignments, setAssignments] = useState([]);
  const [contactsData, setContactsData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Check if user is authenticated
    const loggedInUser = localStorage.getItem('user');

    if (!loggedInUser) {
      // Redirect to signin if not authenticated
      navigate('/signin');
      return;
    }

    try {
      // Parse user data from localStorage
      const userData = JSON.parse(loggedInUser);
      setUser(userData);

      // In a real app, you would fetch this data from an API
      // For demo purposes, we'll use sample data
      fetchUserData(userData.id);
    } catch (error) {
      console.error("Failed to parse user data:", error);
      localStorage.removeItem('user');
      navigate('/signin');
    }
  }, [navigate]);

  // Function to fetch user-specific data
  const fetchUserData = (userId) => {
    setIsLoading(true);

    // Simulating API calls with setTimeout
    setTimeout(() => {
      // Sample data - in a real app, this would come from API calls
      setCourses([
        { id: 1, name: 'Mathematics 101', instructor: 'Dr. Smith', nextLesson: 'Calculus II', progress: 65 },
        { id: 2, name: 'Introduction to Physics', instructor: 'Prof. Johnson', nextLesson: 'Mechanics', progress: 40 },
        { id: 3, name: 'Computer Science Basics', instructor: 'Dr. Williams', nextLesson: 'Data Structures', progress: 80 },
        { id: 4, name: 'English Literature', instructor: 'Prof. Davis', nextLesson: 'Modern Poetry', progress: 55 }
      ]);

      setAssignments([
        { id: 1, title: 'Calculus Problem Set', course: 'Mathematics 101', dueDate: '2023-04-15', status: 'pending' },
        { id: 2, title: 'Physics Lab Report', course: 'Introduction to Physics', dueDate: '2023-04-10', status: 'pending' },
        { id: 3, title: 'Programming Assignment', course: 'Computer Science Basics', dueDate: '2023-04-05', status: 'overdue' },
        { id: 4, title: 'Literature Essay', course: 'English Literature', dueDate: '2023-04-20', status: 'pending' },
        { id: 5, title: 'Midterm Exam', course: 'Mathematics 101', dueDate: '2023-03-25', status: 'completed' }
      ]);

      setContactsData([
        { userId: 'u123', name: 'Prof. Smith', role: 'Mathematics Professor', avatar: '👨‍🏫', status: 'online', unread: 2, lastMessage: 'Please check your latest assignment results.' },
        { userId: 'u124', name: 'John Miller', role: 'Student', avatar: '👨‍🎓', status: 'offline', unread: 0, lastMessage: 'Thanks for the study materials!' },
        { userId: 'u125', name: 'Lisa Johnson', role: 'Teaching Assistant', avatar: '👩‍🏫', status: 'away', unread: 1, lastMessage: 'Can you help with tomorrow\'s lab?' },
        { userId: 'u126', name: 'Academic Advisor', role: 'Advisor', avatar: '👩‍💼', status: 'online', unread: 0, lastMessage: 'Your schedule for next semester is ready.' }
      ]);

      setIsLoading(false);
    }, 1000); // Simulating network delay
  };

  // Navigation functions
  const navigateToChat = (userId) => {
    console.log(`Navigating to chat${userId ? ` with user ${userId}` : ''}`);
    // In a real app, this would change the route or state to show the chat interface
  };

  const startVideoCall = (userId) => {
    console.log(`Starting video call with user ${userId}`);
    // In a real app, this would initiate a video call
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/signin');
  };

  // Show loading state while fetching data
  if (isLoading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading your dashboard...</p>
      </div>
    );
  }

  return (
    <div className="main-content">
      {/* Header */}
      <header className="dashboard-header">
        <button className="menu-toggle" onClick={() => setMenuOpen(true)}>
          ☰
        </button>
        <h1>{activeSection.charAt(0).toUpperCase() + activeSection.slice(1)}</h1>
        <div className="header-actions">
          <button className="chat-button" onClick={() => navigateToChat()}>
            💬
            {contactsData.reduce((count, contact) => count + contact.unread, 0) > 0 && (
              <span className="notification-badge">
                {contactsData.reduce((count, contact) => count + contact.unread, 0)}
              </span>
            )}
          </button>
          <button className="notification-bell">
            🔔
            <span className="notification-badge">3</span>
          </button>
          <button className="logout-button" onClick={handleLogout}>
            Logout
          </button>
        </div>
      </header>

      {/* Content Sections */}
      <div className="dashboard-content">
        {activeSection === 'overview' && (
          <div className="section-overview animate-fade-in">
            <div className="welcome-banner">
              <h2>Welcome back, {user?.name || 'Student'}!</h2>
              <p>Here's what you need to know today:</p>
            </div>

            {/* Quick Stats */}
            <div className="quick-stats">
              <div className="stat-card">
                <h3>Courses</h3>
                <p className="stat-number">{courses.length}</p>
                <p>Active courses</p>
              </div>
              <div className="stat-card">
                <h3>Assignments</h3>
                <p className="stat-number">{assignments.filter(a => a.status === 'pending').length}</p>
                <p>Pending</p>
              </div>
              <div className="stat-card">
                <h3>Overdue</h3>
                <p className="stat-number">{assignments.filter(a => a.status === 'overdue').length}</p>
                <p>Need attention</p>
              </div>
              <div className="stat-card">
                <h3>Average Progress</h3>
                <p className="stat-number">
                  {Math.round(courses.reduce((sum, course) => sum + course.progress, 0) / courses.length)}%
                </p>
                <p>Keep it up!</p>
              </div>
            </div>

            {/* Course Progress Section */}
            <div className="section-container">
              <div className="section-header">
                <h3>Course Progress</h3>
                <button className="view-all-button" onClick={() => setActiveSection('courses')}>
                  View All
                </button>
              </div>
              <div className="course-cards">
                {courses.slice(0, 3).map(course => (
                  <div key={course.id} className="course-card">
                    <h4>{course.name}</h4>
                    <p className="instructor">{course.instructor}</p>
                    <div className="progress-container">
                      <div className="progress-bar">
                        <div className="progress-fill" style={{ width: `${course.progress}%` }}></div>
                      </div>
                      <span className="progress-text">{course.progress}%</span>
                    </div>
                    <p className="next-lesson">Next: {course.nextLesson}</p>
                    <button className="continue-button">Continue Learning</button>
                  </div>
                ))}
              </div>
            </div>

            {/* Upcoming Assignments */}
            <div className="section-container">
              <div className="section-header">
                <h3>Upcoming Assignments</h3>
                <button className="view-all-button" onClick={() => setActiveSection('assignments')}>
                  View All
                </button>
              </div>
              <div className="assignments-list">
                {assignments
                  .filter(assignment => assignment.status !== 'completed')
                  .slice(0, 3)
                  .map(assignment => (
                    <div 
                      key={assignment.id} 
                      className={`assignment-item ${assignment.status === 'overdue' ? 'overdue' : ''}`}
                    >
                      <div className="assignment-info">
                        <h4>{assignment.title}</h4>
                        <p className="course-name">{assignment.course}</p>
                      </div>
                      <div className="assignment-meta">
                        <p className="due-date">
                          Due: {new Date(assignment.dueDate).toLocaleDateString()}
                        </p>
                        <span className={`status-badge ${assignment.status}`}>
                          {assignment.status}
                        </span>
                      </div>
                    </div>
                  ))}
              </div>
            </div>
          </div>
        )}

        {activeSection === 'courses' && (
          <div className="section-courses animate-fade-in">
            <h2>My Courses</h2>
            <div className="courses-grid">
              {courses.map(course => (
                <div key={course.id} className="course-card full">
                  <h3>{course.name}</h3>
                  <p className="instructor">{course.instructor}</p>
                  <div className="progress-container">
                    <div className="progress-bar">
                      <div className="progress-fill" style={{ width: `${course.progress}%` }}></div>
                    </div>
                    <span className="progress-text">{course.progress}%</span>
                  </div>
                  <p className="next-lesson">Next: {course.nextLesson}</p>
                  <div className="course-actions">
                    <button className="primary-button">Continue</button>
                    <button className="secondary-button">Materials</button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {activeSection === 'assignments' && (
          <div className="section-assignments animate-fade-in">
            <h2>Assignments</h2>
            <div className="assignment-filters">
              <button className="filter-button active">All</button>
              <button className="filter-button">Pending</button>
              <button className="filter-button">Overdue</button>
              <button className="filter-button">Completed</button>
            </div>
            <div className="assignments-table">
              <div className="table-header">
                <div className="header-cell">Title</div>
                <div className="header-cell">Course</div>
                <div className="header-cell">Due Date</div>
                <div className="header-cell">Status</div>
                <div className="header-cell">Actions</div>
              </div>
              {assignments.map(assignment => (
                <div 
                  key={assignment.id} 
                  className={`table-row ${assignment.status === 'overdue' ? 'overdue-row' : ''}`}
                >
                  <div className="cell">{assignment.title}</div>
                  <div className="cell">{assignment.course}</div>
                  <div className="cell">{new Date(assignment.dueDate).toLocaleDateString()}</div>
                  <div className="cell">
                    <span className={`status-badge ${assignment.status}`}>
                      {assignment.status}
                    </span>
                  </div>
                  <div className="cell">
                    <button className="action-button">View</button>
                    {assignment.status !== 'completed' && (
                      <button className="action-button submit">Submit</button>
                    )}
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {activeSection === 'contacts' && (
          <div className="section-contacts animate-fade-in">
            <h2>Contacts</h2>
            <div className="contacts-search">
              <input type="text" placeholder="Search contacts..." />
            </div>
            <div className="contacts-list">
              {contactsData.map(contact => (
                <div key={contact.userId} className="contact-card">
                  <div className="contact-avatar">
                    <span className="avatar-icon">{contact.avatar}</span>
                    <span className={`status-indicator ${contact.status}`}></span>
                  </div>
                  <div className="contact-info">
                    <h4>{contact.name}</h4>
                    <p className="contact-role">{contact.role}</p>
                    <p className="last-message">{contact.lastMessage}</p>
                  </div>
                  <div className="contact-actions">
                    <button 
                      className="contact-button message" 
                      onClick={() => navigateToChat(contact.userId)}
                    >
                      💬
                      {contact.unread > 0 && (
                        <span className="contact-badge">{contact.unread}</span>
                      )}
                    </button>
                    <button 
                      className="contact-button call" 
                      onClick={() => startVideoCall(contact.userId)}
                    >
                      📹
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>

      {/* Sidebar Navigation */}
      <div className={`sidebar ${menuOpen ? 'open' : ''}`}>
        <div className="sidebar-header">
          <div className="logo">🎓 EduDash</div>
          <button className="close-menu" onClick={() => setMenuOpen(false)}>×</button>
        </div>
        <div className="user-profile">
          <div className="user-avatar">
            {user?.avatar || '👤'}
          </div>
          <div className="user-info">
            <h3>{user?.name || 'Student Name'}</h3>
            <p>{user?.role || 'Student'}</p>
          </div>
        </div>
        <nav className="sidebar-nav">
          <ul>
            <li className={activeSection === 'overview' ? 'active' : ''}>
              <button onClick={() => {
                setActiveSection('overview');
                setMenuOpen(false);
              }}>
                📊 Overview
              </button>
            </li>
            <li className={activeSection === 'courses' ? 'active' : ''}>
              <button onClick={() => {
                setActiveSection('courses');
                setMenuOpen(false);
              }}>
                📚 My Courses
              </button>
            </li>
            <li className={activeSection === 'assignments' ? 'active' : ''}>
              <button onClick={() => {
                setActiveSection('assignments');
                setMenuOpen(false);
              }}>
                ✅ Assignments
              </button>
            </li>
            <li className={activeSection === 'calendar' ? 'active' : ''}>
              <button onClick={() => {
                setActiveSection('calendar');
                setMenuOpen(false);
              }}>
                📅 Calendar
              </button>
            </li>
            <li className={activeSection === 'contacts' ? 'active' : ''}>
              <button onClick={() => {
                setActiveSection('contacts');
                setMenuOpen(false);
              }}>
                👥 Contacts
              </button>
            </li>
            <li className={activeSection === 'settings' ? 'active' : ''}>
              <button onClick={() => {
                setActiveSection('settings');
                setMenuOpen(false);
              }}>
                ⚙️ Settings
              </button>
            </li>
          </ul>
        </nav>
        <div className="sidebar-footer">
          <button className="help-button">❓ Help & Support</button>
          <button className="logout-button" onClick={handleLogout}>
            🚪 Logout
          </button>
        </div>
      </div>

      {/* Backdrop for mobile */}
      {menuOpen && (
        <div className="sidebar-backdrop" onClick={() => setMenuOpen(false)}></div>
      )}
    </div>
  );
};

export default Dashboard;
