                âš™ï¸ Settings
              </button>
            </li>
          </ul>
        </nav>
        <div className="sidebar-footer">
          <button className="help-button">â“ Help & Support</button>
          <button className="logout-button" onClick={handleLogout}>
            ðŸšª Logout
          </button>
        </div>
      </div>

    {/* Backdrop for mobile */}
    {menuOpen && (
        <div className="backdrop" onClick={() => setMenuOpen(false)}></div>
      )}

      {/* Add Assignment Modal */}
      {showAddAssignmentModal && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h3>Add New Assignment</h3>
              <button className="close-modal" onClick={() => setShowAddAssignmentModal(false)}>Ã—</button>
            </div>
            <form onSubmit={handleSubmitNewAssignment}>
              <div className="form-group">
                <label htmlFor="title">Assignment Title</label>
                <input
                  type="text"
                  id="title"
                  name="title"
                  value={newAssignment.title}
                  onChange={handleAssignmentInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="course">Course</label>
                <select
                  id="course"
                  name="course"
                  value={newAssignment.course}
                  onChange={handleAssignmentInputChange}
                  required
                >
                  <option value="">Select a course</option>
                  {courses.map(course => (
                    <option key={course.id} value={course.name}>{course.name}</option>
                  ))}
                </select>
              </div>
              <div className="form-group">
                <label htmlFor="dueDate">Due Date</label>
                <input
                  type="date"
                  id="dueDate"
                  name="dueDate"
                  value={newAssignment.dueDate}
                  onChange={handleAssignmentInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="estimatedTime">Estimated Time</label>
                <input
                  type="text"
                  id="estimatedTime"
                  name="estimatedTime"
                  placeholder="e.g. 3 hours"
                  value={newAssignment.estimatedTime}
                  onChange={handleAssignmentInputChange}
                />
              </div>
              <div className="form-group">
                <label htmlFor="priority">Priority</label>
                <select
                  id="priority"
                  name="priority"
                  value={newAssignment.priority}
                  onChange={handleAssignmentInputChange}
                >
                  <option value="low">Low</option>
                  <option value="medium">Medium</option>
                  <option value="high">High</option>
                </select>
              </div>
              <div className="form-group">
                <label htmlFor="description">Description</label>
                <textarea
                  id="description"
                  name="description"
                  value={newAssignment.description}
                  onChange={handleAssignmentInputChange}
                  rows="4"
                ></textarea>
              </div>
              <div className="modal-actions">
                <button type="button" className="cancel-button" onClick={() => setShowAddAssignmentModal(false)}>
                  Cancel
                </button>
                <button type="submit" className="submit-button">
                  Add Assignment
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Upload Materials Modal */}
      {showUploadMaterialModal && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h3>Upload Course Materials</h3>
              <button className="close-modal" onClick={() => setShowUploadMaterialModal(false)}>Ã—</button>
            </div>
            <div className="upload-form">
              <div className="upload-area">
                <p>Select files to upload</p>
                <input
                  type="file"
                  multiple
                  onChange={handleFileSelect}
                  ref={fileInputRef}
                  style={{ display: 'none' }}
                />
                <button 
                  className="upload-button"
                  onClick={() => fileInputRef.current.click()}
                >
                  Choose Files
                </button>
              </div>

              {showChat && (
  <div className="chat-overlay">
    <ChatInterface 
      user={user}
      contactsData={contactsData}
      onClose={() => setShowChat(false)}
    />
  </div>
              )
            }

              {uploadedMaterials[currentCourseId] && uploadedMaterials[currentCourseId].length > 0 && (
                <div className="uploaded-files">
                  <h4>Uploaded Files</h4>
                  <ul className="files-list">
                    {uploadedMaterials[currentCourseId].map(file => (
                      <li key={file.id} className="file-item">
                        <div className="file-info">
                          <span className="file-icon">ðŸ“„</span>
                          <div className="file-details">
                            <p className="file-name">{file.name}</p>
                            <span className="file-meta">
                              {formatBytes(file.size)} â€¢ {new Date(file.uploadDate).toLocaleDateString()}
                            </span>
                          </div>
                        </div>
                        <button 
                          className="delete-file" 
                          onClick={() => handleDeleteMaterial(currentCourseId, file.id)}
                        >
                          Ã—
                        </button>
                      </li>
                    ))}
                  </ul>
                </div>
              )}

              <div className="modal-actions">
                <button 
                  className="cancel-button" 
                  onClick={() => setShowUploadMaterialModal(false)}
                >
                  Cancel
                </button>
                <button 
                  className="submit-button"
                  onClick={() => setShowUploadMaterialModal(false)}
                >
                  Done
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
