import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css';
import io from 'socket.io-client';
import { Peer } from 'peerjs';
import './Chat.js';
import './Videocall.js';

// Initialize socket connection
let socket;
let myPeer;

function Dashboard() {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [activeSection, setActiveSection] = useState('overview');
  const [menuOpen, setMenuOpen] = useState(false);

  // Chat and video call states
  const [chatOpen, setChatOpen] = useState(false);
  const [videoCallActive, setVideoCallActive] = useState(false);
  const [selectedContact, setSelectedContact] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [onlineFriends, setOnlineFriends] = useState([]);
  const [incomingCall, setIncomingCall] = useState(null);
  const messagesEndRef = useRef(null);
  const localVideoRef = useRef(null);
  const remoteVideoRef = useRef(null);
  const [isMuted, setIsMuted] = useState(false);
  const [isVideoOff, setIsVideoOff] = useState(false);
  const [callTime, setCallTime] = useState(0);
  const timerRef = useRef(null);
  const [socketConnected, setSocketConnected] = useState(false);
  const [peerConnected, setPeerConnected] = useState(false);

  // User-entered data states
  const [courses, setCourses] = useState([]);
  const [assignments, setAssignments] = useState([]);
  const [todoItems, setTodoItems] = useState([]);

  // New course form
  const [newCourse, setNewCourse] = useState({
    name: '',
    instructor: '',
    nextLesson: '',
    progress: 0
  });

  // New assignment form
  const [newAssignment, setNewAssignment] = useState({
    title: '',
    dueDate: '',
    course: '',
    status: 'pending'
  });

  // New todo form
  const [newTodo, setNewTodo] = useState({
    title: '',
    priority: 'medium',
    dueDate: '',
    completed: false
  });

  // Sample data for contacts with their IDs


  // Initialize WebSocket connection and PeerJS
  useEffect(() => {
    if (user) {
      // Connect to socket server
      socket = io('http://localhost:5000', {
        query: {
          userId: user.studentId,
          userName: user.name
        }
      });

      // Initialize PeerJS for video calls
      myPeer = new Peer(user.studentId, {
        host: 'localhost',
        port: '3001',
        path: '/peerjs'
      });

      // Socket event listeners
      socket.on('connect', () => {
        setSocketConnected(true);
        console.log('Connected to socket server');
      });

      socket.on('user-connected', (users) => {
        // Update online friends list
        const online = contactsData.filter(contact => 
          users.some(u => u.userId === contact.userId)
        );
        setOnlineFriends(online);
      });

      socket.on('receive-message', (data) => {
        if (data.to === user.studentId) {
          setMessages((prevMessages) => [...prevMessages, {
            id: Date.now(),
            senderId: data.from,
            text: data.message,
            timestamp: new Date().toISOString()
          }]);
        }
      });

      socket.on('incoming-call', (data) => {
        const caller = contactsData.find(c => c.userId === data.from);
        if (caller) {
          setIncomingCall({
            from: caller,
            callId: data.callId
          });
        }
      });

      // PeerJS event listeners
      myPeer.on('open', (id) => {
        setPeerConnected(true);
        console.log('Connected to PeerJS server with ID:', id);
      });

      myPeer.on('call', (call) => {
        // Answer the call with our stream
        navigator.mediaDevices.getUserMedia({
          video: true,
          audio: true
        }).then(stream => {
          if (localVideoRef.current) {
            localVideoRef.current.srcObject = stream;
          }

          call.answer(stream);

          // Setup stream for incoming call
          call.on('stream', (remoteStream) => {
            if (remoteVideoRef.current) {
              remoteVideoRef.current.srcObject = remoteStream;
            }
          });
        });
      });

      return () => {
        // Clean up on unmount
        if (socket) socket.disconnect();
        if (myPeer) myPeer.destroy();
        if (timerRef.current) clearInterval(timerRef.current);
      };
    }
  }, [user]);

  // Sample messages data
  const sampleMessages = {
    'STU2023001': [
      { id: 1, senderId: 'STU2023001', text: 'Hello, how are your math assignments coming along?', timestamp: '2023-04-05T10:30:00' },
      { id: 2, senderId: 'me', text: 'Hi Dr. Smith! I\'ve completed the first part, but I have a question about the calculus problem.', timestamp: '2023-04-05T10:32:00' },
      { id: 3, senderId: 'STU2023001', text: 'Sure, what\'s your question?', timestamp: '2023-04-05T10:33:00' },
      { id: 4, senderId: 'me', text: 'In problem 3, I\'m not sure if I should use the chain rule or product rule first.', timestamp: '2023-04-05T10:35:00' },
      { id: 5, senderId: 'STU2023001', text: 'Good question. For that particular problem, start with the product rule, then apply the chain rule to the second term.', timestamp: '2023-04-05T10:38:00' },
    ],
    'STU2023002': [
      { id: 1, senderId: 'STU2023002', text: 'Hey! Are you coming to the study group tonight?', timestamp: '2023-04-04T18:20:00' },
      { id: 2, senderId: 'me', text: 'Yes, I\'ll be there. What time are we meeting?', timestamp: '2023-04-04T18:22:00' },
      { id: 3, senderId: 'STU2023002', text: '7 PM at the library, usual spot!', timestamp: '2023-04-04T18:23:00' },
    ],
    'STU2023004': [
      { id: 1, senderId: 'STU2023004', text: 'Did you get the notes from yesterday\'s class?', timestamp: '2023-04-01T09:15:00' },
      { id: 2, senderId: 'me', text: 'Yes, I did. Do you need them?', timestamp: '2023-04-01T09:20:00' },
      { id: 3, senderId: 'STU2023004', text: 'That would be great! Can you share them?', timestamp: '2023-04-01T09:21:00' },
      { id: 4, senderId: 'me', text: 'Sure, I\'ll send them after class today.', timestamp: '2023-04-01T09:25:00' },
      { id: 5, senderId: 'STU2023004', text: 'Thanks! I appreciate it.', timestamp: '2023-04-01T09:26:00' },
    ]
  };

  const announcements = [
    { id: 1, title: 'Spring Break Schedule', date: '2023-03-30', content: 'Spring break will be from April 8 to April 16.' },
    { id: 2, title: 'New Learning Resources', date: '2023-04-02', content: 'Check out the new learning resources available in the library.' },
    { id: 3, title: 'Campus Event', date: '2023-04-05', content: 'Join us for the annual science fair on April 20.' },
  ];

  useEffect(() => {
    // Check if user is logged in
    const loggedInUser = localStorage.getItem('user');
    if (loggedInUser) {
      const foundUser = JSON.parse(loggedInUser);
      // Ensure the user has a student ID
      if (!foundUser.studentId) {
        foundUser.studentId = 'STU2023012'; // Default ID for demo purposes
      }
      setUser(foundUser);
    } else {
      // Redirect to login if not logged in
      navigate('/signin');
    }

    // Load saved user data from localStorage
    const savedCourses = localStorage.getItem('userCourses');
    if (savedCourses) {
      setCourses(JSON.parse(savedCourses));
    }

    const savedAssignments = localStorage.getItem('userAssignments');
    if (savedAssignments) {
      setAssignments(JSON.parse(savedAssignments));
    }

    const savedTodos = localStorage.getItem('userTodos');
    if (savedTodos) {
      setTodoItems(JSON.parse(savedTodos));
    }

    // Simulate loading data
    setTimeout(() => {
      setIsLoading(false);
    }, 1200);
  }, [navigate]);

  // Save data to localStorage whenever it changes
  useEffect(() => {
    if (courses.length > 0) {
      localStorage.setItem('userCourses', JSON.stringify(courses));
    }
  }, [courses]);

  useEffect(() => {
    if (assignments.length > 0) {
      localStorage.setItem('userAssignments', JSON.stringify(assignments));
    }
  }, [assignments]);

  useEffect(() => {
    if (todoItems.length > 0) {
      localStorage.setItem('userTodos', JSON.stringify(todoItems));
    }
  }, [todoItems]);

  useEffect(() => {
    // Scroll to bottom of messages when new messages are added
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleLogout = () => {
    localStorage.removeItem('user');
    if (socket) socket.disconnect();
    if (myPeer) myPeer.destroy();
    navigate('/signin');
  };

  const openChat = (contactId = null) => {
    setChatOpen(true);
    if (contactId) {
      const contact = contactsData.find(c => c.userId === contactId);
      if (contact) {
        setSelectedContact(contact);

        // Get chat history or load sample messages for demo
        const chatHistory = sampleMessages[contactId] || [];
        setMessages(chatHistory);

        // Mark messages as read
        const updatedContacts = contactsData.map(c => 
          c.userId === contactId ? {...c, unread: 0} : c
        );
        // In a real app, you'd update the contactsData state here
      }
    }
  };

  const closeChat = () => {
    setChatOpen(false);
    setSelectedContact(null);
  };

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (!newMessage.trim() || !selectedContact || !socket) return;

    // Add new message to the conversation
    const newMsg = {
      id: Date.now(),
      senderId: 'me',
      text: newMessage,
      timestamp: new Date().toISOString(),
    };

    setMessages([...messages, newMsg]);

    // Send message via socket
    socket.emit('send-message', {
      from: user.studentId,
      to: selectedContact.userId,
      message: newMessage
    });

    setNewMessage('');
  };

  const formatTime = (timestamp) => {
    const date = new Date(timestamp);
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  const formatCallTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const startVideoCall = (contactId) => {
    const contact = contactsData.find(c => c.userId === contactId);
    if (contact && socket && myPeer) {
      setSelectedContact(contact);
      setVideoCallActive(true);

      // Start timer for call duration
      timerRef.current = setInterval(() => {
        setCallTime(prev => prev + 1);
      }, 1000);

      // Notify the other user about the call
      socket.emit('call-user', {
        from: user.studentId,
        to: contactId,
        callId: Date.now().toString()
      });

      // Initialize video call
      initializeVideoCall(contactId);
    }
  };

  const answerCall = () => {
    if (incomingCall && myPeer) {
      setSelectedContact(incomingCall.from);
      setVideoCallActive(true);
      setIncomingCall(null);

      // Start timer for call duration
      timerRef.current = setInterval(() => {
        setCallTime(prev => prev + 1);
      }, 1000);

      // Initialize video call
      initializeVideoCall(incomingCall.from.userId);
    }
  };

  const rejectCall = () => {
    if (incomingCall && socket) {
      socket.emit('reject-call', {
        from: user.studentId,
        to: incomingCall.from.userId,
        callId: incomingCall.callId
      });

      setIncomingCall(null);
    }
  };
  const initializeVideoCall = async (peerId) => {
    try {
      // Get local video stream
      const stream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true
      });

      if (localVideoRef.current) {
        localVideoRef.current.srcObject = stream;
      }

      // Call the peer
      const call = myPeer.call(peerId, stream);

      // When they answer, add their stream to our video element
      call.on('stream', (remoteStream) => {
        if (remoteVideoRef.current) {
          remoteVideoRef.current.srcObject = remoteStream;
        }
      });

      call.on('close', () => {
        endCall();
      });

      call.on('error', (err) => {
        console.error('PeerJS call error:', err);
        endCall();
      });
    } catch (error) {
      console.error("Error accessing media devices:", error);
    }
  };

  const toggleMute = () => {
    setIsMuted(!isMuted);
    if (localVideoRef.current && localVideoRef.current.srcObject) {
      const audioTracks = localVideoRef.current.srcObject.getAudioTracks();
      audioTracks.forEach(track => {
        track.enabled = isMuted;
      });
    }
  };

  const toggleVideo = () => {
    setIsVideoOff(!isVideoOff);
    if (localVideoRef.current && localVideoRef.current.srcObject) {
      const videoTracks = localVideoRef.current.srcObject.getVideoTracks();
      videoTracks.forEach(track => {
        track.enabled = isVideoOff;
      });
    }
  };

  const endCall = () => {
    if (timerRef.current) clearInterval(timerRef.current);
    setCallTime(0);

    // Stop all tracks
    if (localVideoRef.current && localVideoRef.current.srcObject) {
      localVideoRef.current.srcObject.getTracks().forEach(track => track.stop());
    }

    // Notify the other user that call has ended
    if (socket && selectedContact) {
      socket.emit('end-call', {
        from: user.studentId,
        to: selectedContact.userId
      });
    }

    setVideoCallActive(false);
  };

  // Handle course form changes
  const handleCourseChange = (e) => {
    const { name, value } = e.target;
    setNewCourse(prev => ({
      ...prev,
      [name]: name === 'progress' ? parseInt(value) : value
    }));
  };

  // Add new course
  const handleAddCourse = (e) => {
    e.preventDefault();
    if (!newCourse.name) return;

    const course = {
      id: Date.now(),
      ...newCourse
    };

    setCourses(prev => [...prev, course]);
    setNewCourse({
      name: '',
      instructor: '',
      nextLesson: '',
      progress: 0
    });
  };

  // Handle assignment form changes
  const handleAssignmentChange = (e) => {
    const { name, value } = e.target;
    setNewAssignment(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Add new assignment
  const handleAddAssignment = (e) => {
    e.preventDefault();
    if (!newAssignment.title || !newAssignment.dueDate) return;

    const assignment = {
      id: Date.now(),
      ...newAssignment
    };

    setAssignments(prev => [...prev, assignment]);
    setNewAssignment({
      title: '',
      dueDate: '',
      course: '',
      status: 'pending'
    });
  };

  // Handle todo form changes
  const handleTodoChange = (e) => {
    const { name, value } = e.target;
    setNewTodo(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Add new todo
  const handleAddTodo = (e) => {
    e.preventDefault();
    if (!newTodo.title) return;

    const todo = {
      id: Date.now(),
      ...newTodo,
      completed: false
    };

    setTodoItems(prev => [...prev, todo]);
    setNewTodo({
      title: '',
      priority: 'medium',
      dueDate: '',
      completed: false
    });
  };

  // Toggle todo completion
  const toggleTodoCompletion = (id) => {
    setTodoItems(prev => 
      prev.map(todo => 
        todo.id === id ? { ...todo, completed: !todo.completed } : todo
      )
    );
  };

  // Update assignment status
  const updateAssignmentStatus = (id, status) => {
    setAssignments(prev => 
      prev.map(assignment => 
        assignment.id === id ? { ...assignment, status } : assignment
      )
    );
  };

  // Delete a course
  const deleteCourse = (id) => {
    setCourses(prev => prev.filter(course => course.id !== id));
  };

  // Delete an assignment
  const deleteAssignment = (id) => {
    setAssignments(prev => prev.filter(assignment => assignment.id !== id));
  };

  // Delete a todo item
  const deleteTodo = (id) => {
    setTodoItems(prev => prev.filter(todo => todo.id !== id));
  };

  // Update course progress
  const updateCourseProgress = (id, progress) => {
    setCourses(prev => 
      prev.map(course => 
        course.id === id ? { ...course, progress } : course
      )
    );
  };

  // Sort assignments by due date
  const sortedAssignments = () => {
    return [...assignments].sort((a, b) => new Date(a.dueDate) - new Date(b.dueDate));
  };

  // Get overdue assignments
  const overdueAssignments = () => {
    const today = new Date();
    return assignments.filter(assignment => 
      new Date(assignment.dueDate) < today && assignment.status === 'pending'
    );
  };

  // Get upcoming assignments (due within the next 7 days)
  const upcomingAssignments = () => {
    const today = new Date();
    const nextWeek = new Date();
    nextWeek.setDate(today.getDate() + 7);

    return assignments.filter(assignment => {
      const dueDate = new Date(assignment.dueDate);
      return dueDate >= today && dueDate <= nextWeek && assignment.status === 'pending';
    });
  };

  // Toggle mobile menu
  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  // Format date for display
  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'short', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  // Calculate overall course progress
  const calculateOverallProgress = () => {
    if (courses.length === 0) return 0;
    const totalProgress = courses.reduce((sum, course) => sum + course.progress, 0);
    return Math.round(totalProgress / courses.length);
  };

  // Get courses with upcoming lessons
  const getUpcomingLessons = () => {
    return courses.filter(course => course.nextLesson && course.nextLesson.trim() !== '');
  };

  // Render friends chat section
  const renderFriendsChat = () => {
    return (
      <div className="friends-chat-section">
        <h2>Chat with Friends</h2>

        <div className="connection-status">
          <div className={`status-indicator ${socketConnected ? 'online' : 'offline'}`}></div>
          <span>Chat: {socketConnected ? 'Connected' : 'Disconnected'}</span>

          <div className={`status-indicator ${peerConnected ? 'online' : 'offline'}`}></div>
          <span>Video: {peerConnected ? 'Connected' : 'Disconnected'}</span>
        </div>

        <div className="friends-list">
          <h3>Online Friends</h3>
          {contactsData.map(contact => (
            <div key={contact.userId} className="friend-card">
              <div className="friend-avatar">
                {contact.avatar}
                <span className={`status-indicator ${contact.status}`}></span>
              </div>
              <div className="friend-info">
                <h4>{contact.name}</h4>
                <p>{contact.role}</p>
              </div>
              <div className="friend-actions">
                <button onClick={() => openChat(contact.userId)} className="btn-chat">
                  <i className="fa fa-comment"></i> Chat
                </button>
                <button onClick={() => startVideoCall(contact.userId)} className="btn-video">
                  <i className="fa fa-video-camera"></i> Call
                </button>
              </div>
            </div>
          ))}
        </div>

        <div className="recent-chats">
          <h3>Recent Conversations</h3>
          <ul className="chat-list">
            {Object.keys(sampleMessages).map(contactId => {
              const contact = contactsData.find(c => c.userId === contactId);
              const lastMsg = sampleMessages[contactId][sampleMessages[contactId].length - 1];

              return (
                <li key={contactId} className="chat-item" onClick={() => openChat(contactId)}>
                  <div className="chat-avatar">{contact?.avatar || 'U'}</div>
                  <div className="chat-preview">
                    <div className="chat-name">{contact?.name || 'Unknown'}</div>
                    <div className="chat-last-message">{lastMsg.text}</div>
                  </div>
                  <div className="chat-time">{formatTime(lastMsg.timestamp)}</div>
                </li>
              );
            })}
          </ul>
        </div>
      </div>
    );
  };

  // Render friends video call section
  const renderFriendsVideoCall = () => {
    return (
      <div className="friends-video-section">
        <h2>Video Call with Friends</h2>

        <div className="connection-status">
          <div className={`status-indicator ${peerConnected ? 'online' : 'offline'}`}></div>
          <span>Video Service: {peerConnected ? 'Connected' : 'Disconnected'}</span>
        </div>

        <div className="video-friends-list">
          <h3>Start a Video Call</h3>
          {contactsData.filter(c => c.status === 'online').map(contact => (
            <div key={contact.userId} className="video-friend-card">
              <div className="friend-avatar large">{contact.avatar}</div>
              <div className="friend-info">
                <h4>{contact.name}</h4>
                <p>{contact.role}</p>
                <div className={`status-badge ${contact.status}`}>{contact.status}</div>
              </div>
              <button 
                onClick={() => startVideoCall(contact.userId)} 
                className="btn-video-large"
                disabled={!peerConnected}
              >
                <i className="fa fa-video-camera"></i> Call Now
              </button>
            </div>
          ))}
        </div>

        <div className="video-call-history">
          <h3>Recent Calls</h3>
          <ul className="call-history-list">
            <li className="call-history-item">
              <div className="call-avatar">J</div>
              <div className="call-info">
                <div className="call-name">Jenny Williams</div>
                <div className="call-details">Outgoing  Yesterday, 7:30 PM  12m</div>
              </div>
              <button onClick={() => startVideoCall('STU2023002')} className="btn-call-again">
                <i className="fa fa-phone"></i>
              </button>
            </li>
            <li className="call-history-item">
              <div className="call-avatar">M</div>
              <div className="call-info">
                <div className="call-name">Mike Chen</div>
                <div className="call-details">Incoming  Yesterday, 6:15 PM  8m</div>
              </div>
              <button onClick={() => startVideoCall('STU2023004')} className="btn-call-again">
                <i className="fa fa-phone"></i>
              </button>
            </li>
          </ul>
        </div>
      </div>
    );
  };

  // Render different sections based on activeSection
  const renderSection = () => {
    switch (activeSection) {
      case 'overview':
        return (
          <div className="dashboard-overview">
            <h2>Welcome back, {user?.name || 'Student'}!</h2>

            <div className="stats-container">
              <div className="stat-card">
                <h3>Courses</h3>
                <div className="stat-value">{courses.length}</div>
              </div>
              <div className="stat-card">
                <h3>Overall Progress</h3>
                <div className="stat-value">{calculateOverallProgress()}%</div>
              </div>
              <div className="stat-card">
                <h3>Assignments</h3>
                <div className="stat-value">{assignments.length}</div>
              </div>
              <div className="stat-card">
                <h3>Overdue</h3>
                <div className="stat-value">{overdueAssignments().length}</div>
              </div>
            </div>

            <div className="dashboard-sections">
              <div className="section upcoming-assignments">
                <h3>Upcoming Assignments</h3>
                {upcomingAssignments().length > 0 ? (
                  <ul className="assignment-list">
                    {upcomingAssignments().map(assignment => (
                      <li key={assignment.id} className="assignment-item">
                        <div className="assignment-title">{assignment.title}</div>
                        <div className="assignment-details">
                          <span className="due-date">Due: {formatDate(assignment.dueDate)}</span>
                          <span className="course-name">{assignment.course}</span>
                        </div>
                      </li>
                    ))}
                  </ul>
                ) : (
                  <p className="empty-message">No upcoming assignments</p>
                )}
              </div>

              <div className="section announcements">
                <h3>Announcements</h3>
                <ul className="announcement-list">
                  {announcements.map(announcement => (
                    <li key={announcement.id} className="announcement-item">
                      <div className="announcement-header">
                        <span className="announcement-title">{announcement.title}</span>
                        <span className="announcement-date">{formatDate(announcement.date)}</span>
                      </div>
                      <div className="announcement-content">{announcement.content}</div>
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          </div>
        );
        case 'chat':
          return (
            <div className="chat-section">
              <h2>Student Chat</h2>

              <div className="chat-container">
                <div className="chat-sidebar">
                  <div className="chat-search">
                    <input
                      type="text"
                      placeholder="Search contacts..."
                      value={searchContact}
                      onChange={(e) => setSearchContact(e.target.value)}
                    />
                  </div>

                  <div className="chat-contacts">
                    <h3>Contacts</h3>
                    {contactsData.map(contact => (
                      <div 
                        key={contact.userId} 
                        className={`contact-item ${selectedContact?.userId === contact.userId ? 'active' : ''}`}
                        onClick={() => selectContact(contact)}
                      >
                        <div className="contact-avatar">
                          {contact.avatar}
                          <span className={`status-dot ${contact.status}`}></span>
                        </div>
                        <div className="contact-info">
                          <div className="contact-name">{contact.name}</div>
                          <div className="contact-role">{contact.role}</div>
                        </div>
                        {unreadMessages[contact.userId] > 0 && (
                          <div className="unread-badge">{unreadMessages[contact.userId]}</div>
                        )}
                      </div>
                    ))}
                  </div>
                </div>

                <div className="chat-main">
                  {selectedContact ? (
                    <>
                      <div className="chat-header">
                        <div className="chat-header-user">
                          <div className="contact-avatar large">{selectedContact.avatar}</div>
                          <div className="contact-info">
                            <div className="contact-name">{selectedContact.name}</div>
                            <div className="contact-status">
                              <span className={`status-dot ${selectedContact.status}`}></span>
                              <span>{selectedContact.status === 'online' ? 'Online' : 'Offline'}</span>
                            </div>
                          </div>
                        </div>
                        <div className="chat-header-actions">
                          <button onClick={() => startVideoCall(selectedContact.userId)} className="btn-video-call">
                            <i className="fa fa-video-camera"></i>
                          </button>
                          <button onClick={() => clearChat(selectedContact.userId)} className="btn-clear-chat">
                            <i className="fa fa-trash"></i>
                          </button>
                        </div>
                      </div>

                      <div className="chat-messages" ref={chatMessagesRef}>
                        {messages[selectedContact.userId]?.map((msg, index) => (
                          <div 
                            key={index} 
                            className={`message-item ${msg.sender === user.studentId ? 'outgoing' : 'incoming'}`}
                          >
                            <div className="message-content">{msg.text}</div>
                            <div className="message-time">{formatTime(msg.timestamp)}</div>
                          </div>
                        ))}
                        {/* Display typing indicator */}
                        {isTyping[selectedContact.userId] && (
                          <div className="typing-indicator">
                            <span></span>
                            <span></span>
                            <span></span>
                          </div>
                        )}
                      </div>

                      <div className="chat-input">
                        <textarea 
                          placeholder="Type a message..." 
                          value={newMessage} 
                          onChange={(e) => {
                            setNewMessage(e.target.value);
                            sendTypingIndicator(selectedContact.userId);
                          }}
                          onKeyPress={(e) => {
                            if (e.key === 'Enter' && !e.shiftKey) {
                              e.preventDefault();
                              sendMessage();
                            }
                          }}
                        ></textarea>
                        <div className="chat-input-actions">
                          <button className="btn-attach">
                            <i className="fa fa-paperclip"></i>
                          </button>
                          <button onClick={sendMessage} className="btn-send" disabled={!newMessage.trim()}>
                            <i className="fa fa-paper-plane"></i>
                          </button>
                        </div>
                      </div>
                    </>
                  ) : (
                    <div className="chat-placeholder">
                      <div className="placeholder-icon">
                        <i className="fa fa-comments"></i>
                      </div>
                      <div className="placeholder-text">
                        Select a contact to start chatting
                      </div>
                    </div>
                  )}
                </div>
              </div>

              {/* Video call modal */}
              {videoCallActive && (
                <div className="video-call-modal">
                  <div className="video-call-container">
                    <div className="video-header">
                      <div className="video-caller-info">
                        <span>In call with: {selectedContact?.name}</span>
                        <span className="call-duration">{formatCallTime(callTime)}</span>
                      </div>
                      <button onClick={endCall} className="btn-end-call">
                        <i className="fa fa-phone"></i> End Call
                      </button>
                    </div>

                    <div className="video-screens">
                      <div className="video-screen main">
                        <video ref={remoteVideoRef} autoPlay playsInline className="remote-video"></video>
                        <div className="video-screen-label">
                          {selectedContact?.name}
                        </div>
                      </div>
                      <div className="video-screen self">
                        <video ref={localVideoRef} autoPlay muted playsInline className="local-video"></video>
                        <div className="video-screen-label">
                          You
                        </div>
                      </div>
                    </div>

                    <div className="video-controls">
                      <button onClick={toggleVideo} className={`btn-control ${isVideoOff ? 'active' : ''}`}>
                        <i className={`fa fa-${isVideoOff ? 'video-slash' : 'video'}`}></i>
                      </button>
                      <button onClick={toggleMute} className={`btn-control ${isMuted ? 'active' : ''}`}>
                        <i className={`fa fa-${isMuted ? 'microphone-slash' : 'microphone'}`}></i>
                      </button>
                      <button onClick={toggleFullScreen} className="btn-control">
                        <i className="fa fa-expand"></i>
                      </button>
                    </div>
                  </div>
                </div>
              )}

              {/* Incoming call modal */}
              {incomingCall && (
                <div className="incoming-call-modal">
                  <div className="incoming-call-container">
                    <div className="incoming-call-header">
                      <i className="fa fa-video-camera"></i> Incoming Video Call
                    </div>
                    <div className="incoming-call-body">
                      <div className="caller-avatar">
                        {incomingCallFrom?.avatar || '?'}
                      </div>
                      <div className="caller-name">{incomingCallFrom?.name || 'Unknown Caller'}</div>
                      <div className="call-buttons">
                        <button onClick={answerCall} className="btn-answer">
                          <i className="fa fa-phone"></i> Answer
                        </button>
                        <button onClick={rejectCall} className="btn-reject">
                          <i className="fa fa-phone"></i> Decline
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              )}
            </div>
          );

        case 'video':
          return (
            <div className="video-section">
              <h2>Study Group Video</h2>

              <div className="video-status">
                <div className={`status-indicator ${peerConnected ? 'online' : 'offline'}`}></div>
                <span>Video Service: {peerConnected ? 'Connected' : 'Disconnected'}</span>
              </div>

              <div className="video-container">
                <div className="video-contacts">
                  <h3>Start a Video Call</h3>
                  {contactsData.filter(c => c.status === 'online').map(contact => (
                    <div key={contact.userId} className="video-contact-card">
                      <div className="contact-avatar large">{contact.avatar}</div>
                      <div className="contact-info">
                        <div className="contact-name">{contact.name}</div>
                        <div className="contact-role">{contact.role}</div>
                        <div className="contact-status">
                          <span className={`status-dot ${contact.status}`}></span>
                          <span>{contact.status === 'online' ? 'Available' : 'Unavailable'}</span>
                        </div>
                      </div>
                      <button 
                        onClick={() => startVideoCall(contact.userId)} 
                        className="btn-video-call"
                        disabled={!peerConnected}
                      >
                        <i className="fa fa-video-camera"></i> Start Video Call
                      </button>
                    </div>
                  ))}
                </div>

                <div className="video-scheduler">
                  <h3>Schedule a Study Session</h3>
                  <form onSubmit={handleScheduleStudySession}>
                    <div className="form-group">
                      <label htmlFor="sessionTitle">Session Title</label>
                      <input 
                        type="text" 
                        id="sessionTitle" 
                        name="title" 
                        value={newStudySession.title} 
                        onChange={handleStudySessionChange} 
                        required 
                      />
                    </div>
                    <div className="form-group">
                      <label htmlFor="sessionDate">Date</label>
                      <input 
                        type="date" 
                        id="sessionDate" 
                        name="date" 
                        value={newStudySession.date} 
                        onChange={handleStudySessionChange} 
                        required 
                      />
                    </div>
                    <div className="form-group">
                      <label htmlFor="sessionTime">Time</label>
                      <input 
                        type="time" 
                        id="sessionTime" 
                        name="time" 
                        value={newStudySession.time} 
                        onChange={handleStudySessionChange} 
                        required 
                      />
                    </div>
                    <div className="form-group">
                      <label htmlFor="sessionParticipants">Participants</label>
                      <div className="participants-checkboxes">
                        {contactsData.map(contact => (
                          <div key={contact.userId} className="checkbox-item">
                            <input 
                              type="checkbox" 
                              id={`participant-${contact.userId}`} 
                              value={contact.userId} 
                              checked={newStudySession.participants.includes(contact.userId)}
                              onChange={handleParticipantChange}
                            />
                            <label htmlFor={`participant-${contact.userId}`}>{contact.name}</label>
                          </div>
                        ))}
                      </div>
                    </div>
                    <button type="submit" className="btn-submit">Schedule Session</button>
                  </form>
                </div>
              </div>

              <div className="upcoming-sessions">
                <h3>Upcoming Study Sessions</h3>
                {studySessions.length > 0 ? (
                  <div className="sessions-list">
                    {studySessions.map(session => (
                      <div key={session.id} className="session-card">
                        <div className="session-header">
                          <h4>{session.title}</h4>
                          <div className="session-datetime">
                            {formatDate(session.date)} at {session.time}
                          </div>
                        </div>
                        <div className="session-participants">
                          <strong>Participants:</strong>
                          <div className="participants-avatars">
                            {session.participants.map(participantId => {
                              const participant = contactsData.find(c => c.userId === participantId);
                              return (
                                <div key={participantId} className="participant-avatar" title={participant?.name}>
                                  {participant?.avatar || '?'}
                                </div>
                              );
                            })}
                          </div>
                        </div>
                        <div className="session-actions">
                          <button className="btn-join-session">
                            <i className="fa fa-video-camera"></i> Join Session
                          </button>
                          <button onClick={() => cancelStudySession(session.id)} className="btn-cancel-session">
                            <i className="fa fa-times"></i> Cancel
                          </button>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <p className="empty-message">No upcoming study sessions</p>
                )}
              </div>
            </div>
          );

        case 'profile':
          return (
            <div className="profile-section">
              <h2>Student Profile</h2>

              <div className="profile-container">
                <div className="profile-header">
                  <div className="profile-avatar">
                    {user?.avatar || user?.name?.charAt(0) || 'S'}
                  </div>
                  <div className="profile-info">
                    <h3>{user?.name || 'Student Name'}</h3>
                    <p className="profile-id">Student ID: {user?.studentId || 'N/A'}</p>
                    <p className="profile-email">{user?.email || 'student@example.com'}</p>
                  </div>
                </div>

                <div className="profile-details">
                  <div className="profile-section-block">
                    <h3>Personal Information</h3>
                    <form onSubmit={handleUpdateProfile}>
                      <div className="form-group">
                        <label htmlFor="profileName">Full Name</label>
                        <input 
                          type="text" 
                          id="profileName" 
                          name="name" 
                          value={profileData.name} 
                          onChange={handleProfileChange} 
                        />
                      </div>
                      <div className="form-group">
                        <label htmlFor="profileEmail">Email</label>
                        <input 
                          type="email" 
                          id="profileEmail" 
                          name="email" 
                          value={profileData.email} 
                          onChange={handleProfileChange} 
                        />
                      </div>
                      <div className="form-group">
                        <label htmlFor="profilePhone">Phone</label>
                        <input 
                          type="tel" 
                          id="profilePhone" 
                          name="phone" 
                          value={profileData.phone} 
                          onChange={handleProfileChange} 
                        />
                      </div>
                      <button type="submit" className="btn-update-profile">Update Profile</button>
                    </form>
                  </div>

                  <div className="profile-section-block">
                    <h3>Academic Information</h3>
                    <div className="academic-info">
                      <div className="info-item">
                        <div className="info-label">Major:</div>
                        <div className="info-value">{profileData.major || 'Not set'}</div>
                      </div>
                      <div className="info-item">
                        <div className="info-label">Year:</div>
                        <div className="info-value">{profileData.year || 'Not set'}</div>
                      </div>
                      <div className="info-item">
                        <div className="info-label">GPA:</div>
                        <div className="info-value">{profileData.gpa || 'Not set'}</div>
                      </div>
                      <div className="info-item">
                        <div className="info-label">Advisor:</div>
                        <div className="info-value">{profileData.advisor || 'Not set'}</div>  
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
          )
        }
      }
    }
    export default Dashboard
