// Chat.js
import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import './Chat.css';

function Chat() {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [contacts, setContacts] = useState([]);
  const [selectedContact, setSelectedContact] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const messagesEndRef = useRef(null);

  // Sample data for contacts and messages
  const sampleContacts = [
    { id: 1, name: 'Dr. Smith', role: 'Professor', avatar: 'S', status: 'online', unread: 2 },
    { id: 2, name: 'Jenny Williams', role: 'Student', avatar: 'J', status: 'online', unread: 0 },
    { id: 3, name: 'Prof. Johnson', role: 'Professor', avatar: 'P', status: 'offline', unread: 0 },
    { id: 4, name: 'Mike Chen', role: 'Student', avatar: 'M', status: 'online', unread: 5 },
    { id: 5, name: 'Sarah Davis', role: 'Teaching Assistant', avatar: 'S', status: 'away', unread: 0 },
  ];

  const sampleMessages = {
    1: [
      { id: 1, senderId: 1, text: 'Hello, how are your math assignments coming along?', timestamp: '2023-04-05T10:30:00' },
      { id: 2, senderId: 'me', text: 'Hi Dr. Smith! I\'ve completed the first part, but I have a question about the calculus problem.', timestamp: '2023-04-05T10:32:00' },
      { id: 3, senderId: 1, text: 'Sure, what\'s your question?', timestamp: '2023-04-05T10:33:00' },
      { id: 4, senderId: 'me', text: 'In problem 3, I\'m not sure if I should use the chain rule or product rule first.', timestamp: '2023-04-05T10:35:00' },
      { id: 5, senderId: 1, text: 'Good question. For that particular problem, start with the product rule, then apply the chain rule to the second term.', timestamp: '2023-04-05T10:38:00' },
    ],
    2: [
      { id: 1, senderId: 2, text: 'Hey! Are you coming to the study group tonight?', timestamp: '2023-04-04T18:20:00' },
      { id: 2, senderId: 'me', text: 'Yes, I\'ll be there. What time are we meeting?', timestamp: '2023-04-04T18:22:00' },
      { id: 3, senderId: 2, text: '7 PM at the library, usual spot!', timestamp: '2023-04-04T18:23:00' },
    ],
    4: [
      { id: 1, senderId: 4, text: 'Did you get the notes from yesterday\'s class?', timestamp: '2023-04-01T09:15:00' },
      { id: 2, senderId: 'me', text: 'Yes, I did. Do you need them?', timestamp: '2023-04-01T09:20:00' },
      { id: 3, senderId: 4, text: 'That would be great! Can you share them?', timestamp: '2023-04-01T09:21:00' },
      { id: 4, senderId: 'me', text: 'Sure, I\'ll send them after class today.', timestamp: '2023-04-01T09:25:00' },
      { id: 5, senderId: 4, text: 'Thanks! I appreciate it.', timestamp: '2023-04-01T09:26:00' },
    ]
  };

  useEffect(() => {
    // Check if user is logged in
    const loggedInUser = localStorage.getItem('user');
    if (loggedInUser) {
      const foundUser = JSON.parse(loggedInUser);
      setUser(foundUser);
    } else {
      // Redirect to login if not logged in
      navigate('/signin');
    }

    // Simulate loading data
    setTimeout(() => {
      setContacts(sampleContacts);
      setIsLoading(false);
    }, 800);
  }, [navigate]);

  useEffect(() => {
    // Scroll to bottom of messages
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleContactSelect = (contact) => {
    setSelectedContact(contact);
    // Load messages for this contact
    setMessages(sampleMessages[contact.id] || []);

    // Update contacts to mark messages as read
    setContacts(contacts.map(c => 
      c.id === contact.id ? {...c, unread: 0} : c
    ));
  };

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (!newMessage.trim() || !selectedContact) return;

    // Add new message to the conversation
    const newMsg = {
      id: messages.length + 1,
      senderId: 'me',
      text: newMessage,
      timestamp: new Date().toISOString(),
    };

    setMessages([...messages, newMsg]);
    setNewMessage('');

    // Simulate response (only for demo purposes)
    if (selectedContact.id === 1) {
      setTimeout(() => {
        const response = {
          id: messages.length + 2,
          senderId: selectedContact.id,
          text: 'I see. Let me know if you have any other questions!',
          timestamp: new Date().toISOString(),
        };
        setMessages(prevMessages => [...prevMessages, response]);
      }, 3000);
    }
  };

  const formatTime = (timestamp) => {
    const date = new Date(timestamp);
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  const navigateToDashboard = () => {
    navigate('/dashboard');
  };

  const startVideoCall = () => {
    if (selectedContact) {
      navigate(`/video-call/${selectedContact.id}`, { state: { contact: selectedContact } });
    }
  };

  if (isLoading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading chat...</p>
      </div>
    );
  }

  return (
    <div className="chat-container">
      <div className="chat-sidebar">
        <div className="sidebar-header">
          <h2>Messages</h2>
          <button className="back-to-dashboard" onClick={navigateToDashboard}>
            Back to Dashboard
          </button>
        </div>
        <div className="contacts-search">
          <input type="text" placeholder="Search contacts..." />
        </div>
        <div className="contacts-list">
          {contacts.map(contact => (
            <div 
              key={contact.id} 
              className={`contact-item ${selectedContact?.id === contact.id ? 'active' : ''}`}
              onClick={() => handleContactSelect(contact)}
            >
              <div className="contact-avatar">
                {contact.avatar}
                <span className={`status-indicator ${contact.status}`}></span>
              </div>
              <div className="contact-info">
                <h3>{contact.name}</h3>
                <p>{contact.role}</p>
              </div>
              {contact.unread > 0 && (
                <div className="unread-badge">{contact.unread}</div>
              )}
            </div>
          ))}
        </div>
      </div>

      <div className="chat-main">
        {selectedContact ? (
          <>
            <div className="chat-header">
              <div className="chat-contact-info">
                <div className="contact-avatar">
                  {selectedContact.avatar}
                  <span className={`status-indicator ${selectedContact.status}`}></span>
                </div>
                <div>
                  <h3>{selectedContact.name}</h3>
                  <p>{selectedContact.status === 'online' ? 'Online' : selectedContact.status === 'away' ? 'Away' : 'Offline'}</p>
                </div>
              </div>
              <div className="chat-actions">
                <button className="video-call-btn" onClick={startVideoCall}>
                  <span className="icon">ðŸ“¹</span> Video Call
                </button>
              </div>
            </div>

            <div className="messages-container">
              {messages.map(message => (
                <div 
                  key={message.id} 
                  className={`message ${message.senderId === 'me' ? 'outgoing' : 'incoming'}`}
                >
                  <div className="message-content">
                    <p>{message.text}</p>
                    <span className="message-time">{formatTime(message.timestamp)}</span>
                  </div>
                </div>
              ))}
              <div ref={messagesEndRef} />
            </div>

            <form className="message-input" onSubmit={handleSendMessage}>
              <input 
                type="text" 
                placeholder="Type a message..." 
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
              />
              <button type="submit">Send</button>
            </form>
          </>
        ) : (
          <div className="no-contact-selected">
            <div className="placeholder-content">
