// VideoCall.js
import React, { useState, useEffect, useRef } from 'react';
import { useNavigate, useParams, useLocation } from 'react-router-dom';
import './Videocall.css';

function VideoCall() {
  const navigate = useNavigate();
  const { contactId } = useParams();
  const location = useLocation();
  const contact = location.state?.contact;
  const [user, setUser] = useState(null);
  const [isConnecting, setIsConnecting] = useState(true);
  const [isMuted, setIsMuted] = useState(false);
  const [isVideoOff, setIsVideoOff] = useState(false);
  const [isScreenSharing, setIsScreenSharing] = useState(false);
  const [callTime, setCallTime] = useState(0);
  const localVideoRef = useRef(null);
  const remoteVideoRef = useRef(null);
  const timerRef = useRef(null);
  const originalStreamRef = useRef(null);

  useEffect(() => {
    // Check if user is logged in
    const loggedInUser = localStorage.getItem('user');
    if (loggedInUser) {
      const foundUser = JSON.parse(loggedInUser);
      setUser(foundUser);
    } else {
      // Redirect to login if not logged in
      navigate('/signin');
      return;
    }

    // Start timer for call duration
    timerRef.current = setInterval(() => {
      setCallTime(prev => prev + 1);
    }, 1000);

    // Initialize video call (demo)
    initializeVideoCall();

    return () => {
      if (timerRef.current) clearInterval(timerRef.current);
      // Clean up video streams
      if (localVideoRef.current && localVideoRef.current.srcObject) {
        localVideoRef.current.srcObject.getTracks().forEach(track => track.stop());
      }
    };
  }, [navigate, contactId]);

  const initializeVideoCall = async () => {
    try {
      // Get local video stream
      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: true, 
        audio: true 
      });

      // Store original stream for later use
      originalStreamRef.current = stream;

      if (localVideoRef.current) {
        localVideoRef.current.srcObject = stream;
      }

      // For demo purposes, create a fake remote stream after a delay
      setTimeout(() => {
        setIsConnecting(false);
        // In a real app, this would come from WebRTC connection
        if (remoteVideoRef.current) {
          // In a real app, you'd use the remote peer's stream
          // For this demo, we're using a static image
          remoteVideoRef.current.poster = "/api/placeholder/400/320";
        }
      }, 2000);

    } catch (error) {
      console.error("Error accessing media devices:", error);
      alert("Unable to access camera or microphone. Please check permissions.");
      endCall();
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const toggleMute = () => {
    if (localVideoRef.current && localVideoRef.current.srcObject) {
      const audioTracks = localVideoRef.current.srcObject.getAudioTracks();
      audioTracks.forEach(track => {
        track.enabled = isMuted;
      });
    }
    setIsMuted(!isMuted);
  };

  const toggleVideo = () => {
    if (localVideoRef.current && localVideoRef.current.srcObject) {
      const videoTracks = localVideoRef.current.srcObject.getVideoTracks();
      videoTracks.forEach(track => {
        track.enabled = isVideoOff;
      });
    }
    setIsVideoOff(!isVideoOff);
  };

  const toggleScreenShare = async () => {
    try {
      if (!isScreenSharing) {
        // Store the original stream if not already stored
        if (!originalStreamRef.current && localVideoRef.current && localVideoRef.current.srcObject) {
          originalStreamRef.current = localVideoRef.current.srcObject;
        }

        const screenStream = await navigator.mediaDevices.getDisplayMedia({ 
          video: true
        });

        // Replace with screen sharing stream
        if (localVideoRef.current) {
          localVideoRef.current.srcObject = screenStream;
        }

        // When screen sharing stops
        screenStream.getVideoTracks()[0].onended = () => {
          restoreOriginalStream();
        };

        setIsScreenSharing(true);
      } else {
        restoreOriginalStream();
      }
    } catch (error) {
      console.error("Error sharing screen:", error);
      alert("Unable to share screen. Please check permissions.");
    }
  };

// VideoCall.js (continued)

const restoreOriginalStream = () => {
    if (originalStreamRef.current && localVideoRef.current) {
      localVideoRef.current.srcObject = originalStreamRef.current;
      setIsScreenSharing(false);
    } else {
      // If original stream is not available, reinitialize the video call
      initializeVideoCall();
      setIsScreenSharing(false);
    }
  };

  const endCall = () => {
    if (timerRef.current) clearInterval(timerRef.current);

    // Stop all tracks
    if (localVideoRef.current && localVideoRef.current.srcObject) {
      localVideoRef.current.srcObject.getTracks().forEach(track => track.stop());
    }

    // Navigate back to chat
    navigate('/chat');
  };

  return (
    <div className="video-call-container">
      <div className="call-header">
        <div className="participant-info">
          <h2>Call with {contact?.name || 'Unknown'}</h2>
          <div className="call-timer">{formatTime(callTime)}</div>
        </div>
        <button className="end-call-btn" onClick={endCall}>End Call</button>
      </div>

      <div className="video-grid">
        {isConnecting ? (
          <div className="connecting-status">
            <div className="connecting-spinner"></div>
            <p>Connecting to {contact?.name || 'participant'}...</p>
          </div>
        ) : (
          <>
            <div className="remote-video-container">
              <video 
                ref={remoteVideoRef}
                className="remote-video"
                autoPlay
                playsInline
              />
              <div className="remote-user-name">{contact?.name || 'Remote User'}</div>
            </div>

            <div className="local-video-container">
              <video 
                ref={localVideoRef}
                className="local-video"
                autoPlay
                playsInline
                muted
              />
              <div className="local-user-name">You</div>
              {isMuted && <div className="muted-indicator">üîá</div>}
              {isVideoOff && <div className="video-off-indicator">üö´</div>}
            </div>
          </>
        )}
      </div>

      <div className="call-controls">
        <button 
          className={`control-btn ${isMuted ? 'active' : ''}`} 
          onClick={toggleMute}
        >
          {isMuted ? 'üîá' : 'üîä'}
          <span className="control-label">{isMuted ? 'Unmute' : 'Mute'}</span>
        </button>
        <button 
          className={`control-btn ${isVideoOff ? 'active' : ''}`} 
          onClick={toggleVideo}
        >
          {isVideoOff ? 'üö´' : 'üìπ'}
          <span className="control-label">{isVideoOff ? 'Start Video' : 'Stop Video'}</span>
        </button>
        <button 
          className={`control-btn ${isScreenSharing ? 'active' : ''}`} 
          onClick={toggleScreenShare}
        >
          {isScreenSharing ? 'üñ•Ô∏è' : 'üì§'}
          <span className="control-label">{isScreenSharing ? 'Stop Sharing' : 'Share Screen'}</span>
        </button>
        <button className="control-btn end-call" onClick={endCall}>
          üìû
          <span className="control-label">End Call</span>
        </button>
      </div>
    </div>
  );
}

export default Videocall;
